    public function parse_order_book($orderbook, $symbol, $timestamp = null, $bidKey = 'price', $askKey = 'price', $bidAmountKey = 'amount', $askAmountKey = 'amount', $marketKey = 'orderbook_units') {
        $units = $this->safe_value($orderbook, $marketKey, array());
        $bids = array();
        $asks = array();
        for ($i = 0; $i < count($units); $i++) {
            $unit = $units[$i];
            $bids[] = array( $this->safe_number($unit, 'bid_price'), $this->safe_number($unit, 'bid_size') );
            $asks[] = array( $this->safe_number($unit, 'ask_price'), $this->safe_number($unit, 'ask_size') );
        }
        return array(
            'symbol' => $symbol,
            'bids' => $this->sort_by($bids, 0, true),
            'asks' => $this->sort_by($asks, 0),
            'timestamp' => $timestamp,
            'datetime' => $this->iso8601($timestamp),
            'nonce' => null,
        );
    }

