    public function parse_order(array $order, ?array $market = null): array {
        $timestamp = $this->parse8601($this->safe_string($order, 'created_at'));
        $status = $this->parse_order_status($this->safe_string($order, 'state'));
        $symbol = $this->safe_symbol($this->safe_string($order, 'market'), $market);
        $side = $this->safe_string_lower($order, 'side') === 'bid' ? 'buy' : 'sell';
        $type = $this->safe_string_lower($order, 'ord_type');
        $price = $this->safe_string($order, 'price');
        $amount = $this->safe_string($order, 'volume');
        $remaining = $this->safe_string($order, 'remaining_volume');
        $filled = $this->safe_string($order, 'executed_volume');
        $id = $this->safe_string($order, 'uuid');
        return $this->safe_order(array(
            'id' => $id,
            'clientOrderId' => null,
            'timestamp' => $timestamp,
            'datetime' => $this->iso8601($timestamp),
            'lastTradeTimestamp' => null,
            'status' => $status,
            'symbol' => $symbol,
            'type' => $type,
            'timeInForce' => null,
            'postOnly' => null,
            'side' => $side,
            'price' => $price,
            'stopPrice' => null,
            'triggerPrice' => null,
            'amount' => $amount,
            'filled' => $filled,
            'remaining' => $remaining,
            'cost' => null,
            'trades' => null,
            'fee' => array(
                'currency' => null,
                'cost' => $this->safe_string($order, 'paid_fee'),
            ),
            'info' => $order,
        ), $market);
    }

    public function parse_order_status(?string $status) {
        $statuses = array(
            'wait' => 'open',
            'done' => 'closed',
            'cancel' => 'canceled',
        );
        return $this->safe_string($statuses, $status, $status);
    }

    public function sign($path, $api = 'public', $method = 'GET', $params = array (), $headers = null, $body = null) {
        $url = $this->urls['api'][$api] . '/' . $this->implode_params($path, $params);
        $query = $this->omit($params, $this->extract_params($path));
        if ($api === 'public') {
            if ($query) {
                $url .= '?' . $this->urlencode($query);
            }
        } else {
            $this->check_required_credentials();
            $nonce = $this->uuid();
            $timestamp = $this->milliseconds();
            $payload = array(
                'access_key' => $this->apiKey,
                'nonce' => $nonce,
                'timestamp' => $timestamp,
            );
            if ($query) {
                if ($method === 'GET' || $method === 'DELETE') {
                    $queryString = $this->urlencode($query);
                    $url .= '?' . $queryString;
                    $payload['query_hash'] = $this->hash($this->encode($queryString), 'sha512');
                } else {
                    $body = $this->json($query);
                    $payload['query_hash'] = $this->hash($this->encode($this->urlencode($query)), 'sha512');
                }
            }
            $token = $this->jwt($payload, $this->encode($this->secret), 'sha256');
            $headers = array(
                'Authorization' => 'Bearer ' . $token,
            );
            if ($method === 'POST') {
                $headers['Content-Type'] = 'application/json';
            }
        }
        return array( 'url' => $url, 'method' => $method, 'body' => $body, 'headers' => $headers );
    }
