            //                      "low_price":"0.128570",
            //                      "legal_coin_price":"0.13017401"
            //                  }
            //              )
            //          }
            //      }
            //
            $data = $this->safe_value($response, 'data', array());
            $tickers = $this->safe_value($data, 'tickers', array());
            // fails in naming for contract $tickers 'contract_symbol'
            $tickersById = null;
