                $until = $this->safe_integer_2($params, 'until', 'endTime');
                if ($until !== null) {
                    $params = $this->omit($params, array( 'endTime' ));
                    $request['endTime'] = $until;
                }
                $response = Async\await($this->privatePostSpotV4QueryOpenOrders ($this->extend($request, $params)));
            } elseif ($type === 'swap') {
