    public function parse_transaction($transaction, $currency = null) {
        //
        //    {
        //        'transactID' => 'ffe699c2-95ee-4c13-91f9-0faf41daec25',
        //        'account' => 123456,
        //        'currency' => 'XBt',
        //        'network':'', // "tron" for USDt, etc...
        //        'transactType' => 'Withdrawal',
        //        'amount' => -100100000,
        //        'fee' => 100000,
        //        'transactStatus' => 'Completed',
        //        'address' => '385cR5DM96n1HvBDMzLHPYcw89fZAXULJP',
        //        'tx' => '3BMEXabcdefghijklmnopqrstuvwxyz123',
        //        'text' => '',
        //        'transactTime' => '2019-01-02T01:00:00.000Z',
        //        'walletBalance' => 99900000, // this field might be inexistent
        //        'marginBalance' => None, // this field might be inexistent
        //        'timestamp' => '2019-01-02T13:00:00.000Z'
        //    }
        //
        $currencyId = $this->safe_string($transaction, 'currency');
        $currency = $this->safe_currency($currencyId, $currency);
        // For deposits, $transactTime == $timestamp
        // For withdrawals, $transactTime is submission, $timestamp is processed
        $transactTime = $this->parse8601($this->safe_string($transaction, 'transactTime'));
        $timestamp = $this->parse8601($this->safe_string($transaction, 'timestamp'));
        $type = $this->safe_string_lower($transaction, 'transactType');
        // Deposits have no from $address or to $address, withdrawals have both
        $address = null;
        $addressFrom = null;
        $addressTo = null;
        if ($type === 'withdrawal') {
            $address = $this->safe_string($transaction, 'address');
            $addressFrom = $this->safe_string($transaction, 'tx');
            $addressTo = $address;
        } elseif ($type === 'deposit') {
            $addressTo = $this->safe_string($transaction, 'address');
            $addressFrom = $this->safe_string($transaction, 'tx');
        }
        $amountString = $this->safe_string($transaction, 'amount');
        $scale = ($currency['code'] === 'BTC') ? '1e8' : '1e6';
        $amountString = Precise::string_div(Precise::string_abs($amountString), $scale);
        $feeCostString = $this->safe_string($transaction, 'fee');
        $feeCostString = Precise::string_div($feeCostString, $scale);
        $status = $this->safe_string($transaction, 'transactStatus');
        if ($status !== null) {
            $status = $this->parse_transaction_status($status);
        }
        return array(
            'info' => $transaction,
            'id' => $this->safe_string($transaction, 'transactID'),
            'txid' => $this->safe_string($transaction, 'tx'),
            'type' => $type,
            'currency' => $currency['code'],
            'network' => $this->safe_string($transaction, 'network'),
            'amount' => $this->parse_number($amountString),
            'status' => $status,
            'timestamp' => $transactTime,
            'datetime' => $this->iso8601($transactTime),
            'address' => $address,
            'addressFrom' => $addressFrom,
            'addressTo' => $addressTo,
            'tag' => null,
            'tagFrom' => null,
            'tagTo' => null,
            'updated' => $timestamp,
            'comment' => null,
            'fee' => array(
                'currency' => $currency['code'],
                'cost' => $this->parse_number($feeCostString),
                'rate' => null,
            ),
        );
    }

    public function fetch_ticker(string $symbol, $params = array ()) {
        return Async\async(function () use ($symbol, $params) {
            /**
             * fetches a price $ticker, a statistical calculation with the information calculated over the past 24 hours for a specific $market
             * @param {string} $symbol unified $symbol of the $market to fetch the $ticker for
             * @param {array} $params extra parameters specific to the bitmex api endpoint
             * @return {array} a ~@link https://docs.ccxt.com/#/?id=$ticker-structure $ticker structure~
             */
            Async\await($this->load_markets());
            $market = $this->market($symbol);
            $tickers = Async\await($this->fetch_tickers([ $market['symbol'] ], $params));
            $ticker = $this->safe_value($tickers, $market['symbol']);
            if ($ticker === null) {
                throw new BadSymbol($this->id . ' fetchTicker() $symbol ' . $symbol . ' not found');
            }
            return $ticker;
        }) ();
    }

    public function fetch_tickers(?array $symbols = null, $params = array ()) {
        return Async\async(function () use ($symbols, $params) {
            /**
             * fetches price tickers for multiple markets, statistical calculations with the information calculated over the past 24 hours each market
             * @param {[string]|null} $symbols unified $symbols of the markets to fetch the $ticker for, all market tickers are returned if not assigned
             * @param {array} $params extra parameters specific to the bitmex api endpoint
             * @return {array} a dictionary of ~@link https://docs.ccxt.com/#/?id=$ticker-structure $ticker structures~
             */
            Async\await($this->load_markets());
            $symbols = $this->market_symbols($symbols);
            $response = Async\await($this->publicGetInstrumentActiveAndIndices ($params));
            // same $response "fetchMarkets"
            $result = array();
            for ($i = 0; $i < count($response); $i++) {
                $ticker = $this->parse_ticker($response[$i]);
                $symbol = $this->safe_string($ticker, 'symbol');
                if ($symbol !== null) {
                    $result[$symbol] = $ticker;
                }
            }
            return $this->filter_by_array($result, 'symbol', $symbols);
        }) ();
    }

    public function parse_ticker($ticker, $market = null) {
        //
        //     {                         $symbol => "ETHH19",
        //                           rootSymbol => "ETH",
        //                                state => "Open",
        //                                  typ => "FFCCSX",
        //                              listing => "2018-12-17T04:00:00.000Z",
        //                                front => "2019-02-22T12:00:00.000Z",
