    public function parse_ohlcv($ohlcv, $market = null) {
        //
        //     {
        //         "timestamp":"2015-09-25T13:38:00.000Z",
        //         "symbol":"XBTUSD",
        //         "open":237.45,
        //         "high":237.45,
        //         "low":237.45,
        //         "close":237.45,
        //         "trades":0,
        //         "volume":0,
        //         "vwap":null,
        //         "lastSize":null,
        //         "turnover":0,
        //         "homeNotional":0,
        //         "foreignNotional":0
        //     }
        //
        return array(
            $this->parse8601($this->safe_string($ohlcv, 'timestamp')),
            $this->safe_number($ohlcv, 'open'),
            $this->safe_number($ohlcv, 'high'),
            $this->safe_number($ohlcv, 'low'),
            $this->safe_number($ohlcv, 'close'),
            $this->safe_number($ohlcv, 'volume'),
        );
    }

    public function fetch_ohlcv(string $symbol, $timeframe = '1m', ?int $since = null, ?int $limit = null, $params = array ()) {
        return Async\async(function () use ($symbol, $timeframe, $since, $limit, $params) {
            /**
             * fetches historical candlestick data containing the open, high, low, and close price, and the volume of a $market
             * @param {string} $symbol unified $symbol of the $market to fetch OHLCV data for
             * @param {string} $timeframe the length of time each candle represents
             * @param {int|null} $since $timestamp in ms of the earliest candle to fetch
             * @param {int|null} $limit the maximum amount of candles to fetch
             * @param {array} $params extra parameters specific to the bitmex api endpoint
             * @return {[[int]]} A list of candles ordered, open, high, low, close, volume
             */
            Async\await($this->load_markets());
            // send JSON key/value pairs, such as array("key" => "value")
            // $filter by individual fields and do advanced queries on timestamps
            // $filter = array( 'key' => 'value' );
            // send a bare series (e.g. XBU) to nearest expiring contract in that series
            // you can also send a $timeframe, e.g. XBU:monthly
            // timeframes => daily, weekly, monthly, quarterly, and biquarterly
            $market = $this->market($symbol);
            $request = array(
                'symbol' => $market['id'],
                'binSize' => $this->safe_string($this->timeframes, $timeframe, $timeframe),
                'partial' => true,     // true == include yet-incomplete current bins
                // 'filter' => $filter, // $filter by individual fields and do advanced queries
                // 'columns' => array(),    // will return all columns if omitted
                // 'start' => 0,       // starting point for results (wtf?)
                // 'reverse' => false, // true == newest first
                // 'endTime' => '',    // ending date $filter for results
            );
            if ($limit !== null) {
                $request['count'] = $limit; // default 100, max 500
            }
            $duration = $this->parse_timeframe($timeframe) * 1000;
            $fetchOHLCVOpenTimestamp = $this->safe_value($this->options, 'fetchOHLCVOpenTimestamp', true);
            // if $since is not set, they will return candles starting from 2017-01-01
            if ($since !== null) {
                $timestamp = $since;
                if ($fetchOHLCVOpenTimestamp) {
                    $timestamp = $this->sum($timestamp, $duration);
                }
                $ymdhms = $this->ymdhms($timestamp);
                $request['startTime'] = $ymdhms; // starting date $filter for results
            } else {
                $request['reverse'] = true;
            }
            $response = Async\await($this->publicGetTradeBucketed (array_merge($request, $params)));
            //
            //     array(
            //         array("timestamp":"2015-09-25T13:38:00.000Z","symbol":"XBTUSD","open":237.45,"high":237.45,"low":237.45,"close":237.45,"trades":0,"volume":0,"vwap":null,"lastSize":null,"turnover":0,"homeNotional":0,"foreignNotional":0),
            //         array("timestamp":"2015-09-25T13:39:00.000Z","symbol":"XBTUSD","open":237.45,"high":237.45,"low":237.45,"close":237.45,"trades":0,"volume":0,"vwap":null,"lastSize":null,"turnover":0,"homeNotional":0,"foreignNotional":0),
            //         array("timestamp":"2015-09-25T13:40:00.000Z","symbol":"XBTUSD","open":237.45,"high":237.45,"low":237.45,"close":237.45,"trades":0,"volume":0,"vwap":null,"lastSize":null,"turnover":0,"homeNotional":0,"foreignNotional":0)
            //     )
            //
            $result = $this->parse_ohlcvs($response, $market, $timeframe, $since, $limit);
            if ($fetchOHLCVOpenTimestamp) {
                // bitmex returns the candle's close $timestamp - https://github.com/ccxt/ccxt/issues/4446
                // we can emulate the open $timestamp by shifting all the timestamps one place
                // so the previous close becomes the current open, and we drop the first candle
                for ($i = 0; $i < count($result); $i++) {
                    $result[$i][0] = $result[$i][0] - $duration;
                }
            }
            return $result;
        }) ();
    }

    public function parse_trade($trade, $market = null) {
        //
        // fetchTrades (public)
        //
        //     {
        //         $timestamp => '2018-08-28T00:00:02.735Z',
        //         $symbol => 'XBTUSD',
        //         $side => 'Buy',
        //         size => 2000,
        //         price => 6906.5,
        //         tickDirection => 'PlusTick',
        //         trdMatchID => 'b9a42432-0a46-6a2f-5ecc-c32e9ca4baf8',
        //         grossValue => 28958000,
        //         homeNotional => 0.28958,
        //         foreignNotional => 2000
        //     }
        //
        // fetchMyTrades (private)
        //
        //     {
        //         "execID" => "string",
        //         "orderID" => "string",
        //         "clOrdID" => "string",
        //         "clOrdLinkID" => "string",
        //         "account" => 0,
        //         "symbol" => "string",
        //         "side" => "string",
        //         "lastQty" => 0,
        //         "lastPx" => 0,
        //         "underlyingLastPx" => 0,
        //         "lastMkt" => "string",
        //         "lastLiquidityInd" => "string",
        //         "simpleOrderQty" => 0,
        //         "orderQty" => 0,
        //         "price" => 0,
        //         "displayQty" => 0,
        //         "stopPx" => 0,
        //         "pegOffsetValue" => 0,
        //         "pegPriceType" => "string",
        //         "currency" => "string",
        //         "settlCurrency" => "string",
        //         "execType" => "string",
        //         "ordType" => "string",
        //         "timeInForce" => "string",
        //         "execInst" => "string",
        //         "contingencyType" => "string",
        //         "exDestination" => "string",
        //         "ordStatus" => "string",
        //         "triggered" => "string",
        //         "workingIndicator" => true,
        //         "ordRejReason" => "string",
        //         "simpleLeavesQty" => 0,
        //         "leavesQty" => 0,
        //         "simpleCumQty" => 0,
        //         "cumQty" => 0,
        //         "avgPx" => 0,
        //         "commission" => 0,
        //         "tradePublishIndicator" => "string",
        //         "multiLegReportingType" => "string",
        //         "text" => "string",
        //         "trdMatchID" => "string",
        //         "execCost" => 0,
        //         "execComm" => 0,
        //         "homeNotional" => 0,
        //         "foreignNotional" => 0,
        //         "transactTime" => "2019-03-05T12:47:02.762Z",
        //         "timestamp" => "2019-03-05T12:47:02.762Z"
        //     }
        //
        $timestamp = $this->parse8601($this->safe_string($trade, 'timestamp'));
        $priceString = $this->safe_string_2($trade, 'avgPx', 'price');
        $amountString = $this->safe_string_2($trade, 'size', 'lastQty');
        $execCost = $this->safe_string($trade, 'execCost');
        $costString = Precise::string_div(Precise::string_abs($execCost), '1e8');
        $id = $this->safe_string($trade, 'trdMatchID');
        $order = $this->safe_string($trade, 'orderID');
        $side = $this->safe_string_lower($trade, 'side');
        // price * amount doesn't work for all symbols (e.g. XBT, ETH)
        $fee = null;
        $feeCostString = Precise::string_div($this->safe_string($trade, 'execComm'), '1e8');
        if ($feeCostString !== null) {
            $currencyId = $this->safe_string($trade, 'settlCurrency');
            $feeCurrencyCode = $this->safe_currency_code($currencyId);
            $feeRateString = $this->safe_string($trade, 'commission');
            $fee = array(
                'cost' => $feeCostString,
                'currency' => $feeCurrencyCode,
                'rate' => $feeRateString,
            );
        }
        // Trade or Funding
        $execType = $this->safe_string($trade, 'execType');
        $takerOrMaker = null;
        if ($feeCostString !== null && $execType === 'Trade') {
            $takerOrMaker = Precise::string_lt($feeCostString, '0') ? 'maker' : 'taker';
        }
        $marketId = $this->safe_string($trade, 'symbol');
        $symbol = $this->safe_symbol($marketId, $market);
        $type = $this->safe_string_lower($trade, 'ordType');
        return $this->safe_trade(array(
            'info' => $trade,
            'timestamp' => $timestamp,
            'datetime' => $this->iso8601($timestamp),
            'symbol' => $symbol,
            'id' => $id,
            'order' => $order,
            'type' => $type,
            'takerOrMaker' => $takerOrMaker,
            'side' => $side,
            'price' => $priceString,
            'cost' => $costString,
            'amount' => $amountString,
            'fee' => $fee,
        ), $market);
    }

    public function parse_order_status($status) {
        $statuses = array(
            'New' => 'open',
            'PartiallyFilled' => 'open',
            'Filled' => 'closed',
            'DoneForDay' => 'open',
            'Canceled' => 'canceled',
            'PendingCancel' => 'open',
            'PendingNew' => 'open',
            'Rejected' => 'rejected',
            'Expired' => 'expired',
            'Stopped' => 'open',
            'Untriggered' => 'open',
            'Triggered' => 'open',
        );
        return $this->safe_string($statuses, $status, $status);
    }

    public function parse_time_in_force($timeInForce) {
        $timeInForces = array(
            'Day' => 'Day',
            'GoodTillCancel' => 'GTC',
            'ImmediateOrCancel' => 'IOC',
            'FillOrKill' => 'FOK',
        );
        return $this->safe_string($timeInForces, $timeInForce, $timeInForce);
    }

    public function parse_order($order, $market = null) {
        //
        //     {
        //         "orderID":"56222c7a-9956-413a-82cf-99f4812c214b",
        //         "clOrdID":"",
        //         "clOrdLinkID":"",
        //         "account":1455728,
        //         "symbol":"XBTUSD",
        //         "side":"Sell",
        //         "simpleOrderQty":null,
        //         "orderQty":1,
        //         "price":40000,
        //         "displayQty":null,
        //         "stopPx":null,
        //         "pegOffsetValue":null,
        //         "pegPriceType":"",
        //         "currency":"USD",
        //         "settlCurrency":"XBt",
        //         "ordType":"Limit",
        //         "timeInForce":"GoodTillCancel",
        //         "execInst":"",
        //         "contingencyType":"",
        //         "exDestination":"XBME",
        //         "ordStatus":"New",
        //         "triggered":"",
        //         "workingIndicator":true,
        //         "ordRejReason":"",
        //         "simpleLeavesQty":null,
        //         "leavesQty":1,
        //         "simpleCumQty":null,
        //         "cumQty":0,
        //         "avgPx":null,
        //         "multiLegReportingType":"SingleSecurity",
        //         "text":"Submitted via API.",
        //         "transactTime":"2021-01-02T21:38:49.246Z",
        //         "timestamp":"2021-01-02T21:38:49.246Z"
        //     }
        //
        $status = $this->parse_order_status($this->safe_string($order, 'ordStatus'));
        $marketId = $this->safe_string($order, 'symbol');
        $symbol = $this->safe_symbol($marketId, $market);
        $timestamp = $this->parse8601($this->safe_string($order, 'timestamp'));
        $lastTradeTimestamp = $this->parse8601($this->safe_string($order, 'transactTime'));
        $price = $this->safe_string($order, 'price');
        $amount = $this->safe_string($order, 'orderQty');
        $filled = $this->safe_string($order, 'cumQty');
        $average = $this->safe_string($order, 'avgPx');
        $id = $this->safe_string($order, 'orderID');
        $type = $this->safe_string_lower($order, 'ordType');
        $side = $this->safe_string_lower($order, 'side');
        $clientOrderId = $this->safe_string($order, 'clOrdID');
        $timeInForce = $this->parse_time_in_force($this->safe_string($order, 'timeInForce'));
        $stopPrice = $this->safe_number($order, 'stopPx');
        $execInst = $this->safe_string($order, 'execInst');
        $postOnly = null;
        if ($execInst !== null) {
            $postOnly = ($execInst === 'ParticipateDoNotInitiate');
        }
        return $this->safe_order(array(
            'info' => $order,
            'id' => $id,
            'clientOrderId' => $clientOrderId,
            'timestamp' => $timestamp,
            'datetime' => $this->iso8601($timestamp),
            'lastTradeTimestamp' => $lastTradeTimestamp,
            'symbol' => $symbol,
            'type' => $type,
            'timeInForce' => $timeInForce,
            'postOnly' => $postOnly,
            'side' => $side,
            'price' => $price,
            'stopPrice' => $stopPrice,
            'triggerPrice' => $stopPrice,
            'amount' => $amount,
            'cost' => null,
            'average' => $average,
            'filled' => $filled,
            'remaining' => null,
            'status' => $status,
            'fee' => null,
            'trades' => null,
        ), $market);
    }

    public function fetch_trades(string $symbol, ?int $since = null, ?int $limit = null, $params = array ()) {
        return Async\async(function () use ($symbol, $since, $limit, $params) {
            /**
             * get the list of most recent trades for a particular $symbol
             * @param {string} $symbol unified $symbol of the $market to fetch trades for
             * @param {int|null} $since timestamp in ms of the earliest trade to fetch
             * @param {int|null} $limit the maximum amount of trades to fetch
             * @param {array} $params extra parameters specific to the bitmex api endpoint
             * @return {[array]} a list of ~@link https://docs.ccxt.com/en/latest/manual.html?#public-trades trade structures~
             */
            Async\await($this->load_markets());
            $market = $this->market($symbol);
            $request = array(
                'symbol' => $market['id'],
            );
            if ($since !== null) {
                $request['startTime'] = $this->iso8601($since);
            } else {
                // by default reverse=false, i.e. trades are fetched $since the time of $market inception (year 2015 for XBTUSD)
                $request['reverse'] = true;
            }
            if ($limit !== null) {
                $request['count'] = $limit;
            }
            $response = Async\await($this->publicGetTrade (array_merge($request, $params)));
            //
            //     array(
            //         array(
            //             timestamp => '2018-08-28T00:00:02.735Z',
            //             $symbol => 'XBTUSD',
            //             side => 'Buy',
            //             size => 2000,
            //             price => 6906.5,
            //             tickDirection => 'PlusTick',
            //             trdMatchID => 'b9a42432-0a46-6a2f-5ecc-c32e9ca4baf8',
            //             grossValue => 28958000,
            //             homeNotional => 0.28958,
            //             foreignNotional => 2000
            //         ),
            //         array(
            //             timestamp => '2018-08-28T00:00:03.778Z',
            //             $symbol => 'XBTUSD',
            //             side => 'Sell',
            //             size => 1000,
            //             price => 6906,
            //             tickDirection => 'MinusTick',
            //             trdMatchID => '0d4f1682-5270-a800-569b-4a0eb92db97c',
            //             grossValue => 14480000,
            //             homeNotional => 0.1448,
            //             foreignNotional => 1000
            //         ),
            //     )
            //
            return $this->parse_trades($response, $market, $since, $limit);
        }) ();
    }

    public function create_order(string $symbol, string $type, string $side, $amount, $price = null, $params = array ()) {
        return Async\async(function () use ($symbol, $type, $side, $amount, $price, $params) {
            /**
             * create a trade order
             * @param {string} $symbol unified $symbol of the $market to create an order in
             * @param {string} $type 'market' or 'limit'
             * @param {string} $side 'buy' or 'sell'
             * @param {float} $amount how much of currency you want to trade in units of base currency
             * @param {float|null} $price the $price at which the order is to be fullfilled, in units of the quote currency, ignored in $market orders
             * @param {array} $params extra parameters specific to the bitmex api endpoint
             * @return {array} an ~@link https://docs.ccxt.com/#/?id=order-structure order structure~
             */
            Async\await($this->load_markets());
            $market = $this->market($symbol);
            $orderType = $this->capitalize($type);
            $reduceOnly = $this->safe_value($params, 'reduceOnly');
            if ($reduceOnly !== null) {
                if (($market['type'] !== 'swap') && ($market['type'] !== 'future')) {
                    throw new InvalidOrder($this->id . ' createOrder() does not support $reduceOnly for ' . $market['type'] . ' orders, $reduceOnly orders are supported for swap and future markets only');
                }
            }
            $brokerId = $this->safe_string($this->options, 'brokerId', 'CCXT');
            $request = array(
                'symbol' => $market['id'],
                'side' => $this->capitalize($side),
                'orderQty' => floatval($this->amount_to_precision($symbol, $amount)), // lot size multiplied by the number of contracts
                'ordType' => $orderType,
                'text' => $brokerId,
            );
            if (($orderType === 'Stop') || ($orderType === 'StopLimit') || ($orderType === 'MarketIfTouched') || ($orderType === 'LimitIfTouched')) {
                $stopPrice = $this->safe_number_2($params, 'stopPx', 'stopPrice');
                if ($stopPrice === null) {
                    throw new ArgumentsRequired($this->id . ' createOrder() requires a stopPx or $stopPrice parameter for the ' . $orderType . ' order type');
                } else {
                    $request['stopPx'] = floatval($this->price_to_precision($symbol, $stopPrice));
                    $params = $this->omit($params, array( 'stopPx', 'stopPrice' ));
                }
            }
            if (($orderType === 'Limit') || ($orderType === 'StopLimit') || ($orderType === 'LimitIfTouched')) {
                $request['price'] = floatval($this->price_to_precision($symbol, $price));
            }
            $clientOrderId = $this->safe_string_2($params, 'clOrdID', 'clientOrderId');
            if ($clientOrderId !== null) {
                $request['clOrdID'] = $clientOrderId;
                $params = $this->omit($params, array( 'clOrdID', 'clientOrderId' ));
            }
            $response = Async\await($this->privatePostOrder (array_merge($request, $params)));
            return $this->parse_order($response, $market);
        }) ();
