            //                         array("volume":"10000000","maker":"0.00420","taker":"0.00546"),
            //                         array("volume":"15000000","maker":"0.00400","taker":"0.00520"),
            //                         array("volume":"35000000","maker":"0.00370","taker":"0.00481"),
