                $result[] = array_merge(array(
                    'id' => $id,
                    'symbol' => $base . '/' . $quote,
                    'base' => $base,
                    'quote' => $quote,
                    'settle' => null,
                    'baseId' => $baseId,
                    'quoteId' => $quoteId,
                    'settleId' => null,
                    'type' => 'spot',
                    'spot' => true,
                    'margin' => false,
                    'swap' => false,
                    'future' => false,
                    'option' => false,
                    'active' => null,
                    'contract' => false,
                    'linear' => null,
                    'inverse' => null,
                    'taker' => $taker,
                    'maker' => $maker,
                    'contractSize' => null,
                    'expiry' => null,
                    'expiryDatetime' => null,
                    'strike' => null,
                    'optionType' => null,
                    'precision' => array(
                        'amount' => $this->safe_number($this->options['precision'], $base, $this->options['defaultPrecision']),
                        'price' => $this->safe_number($market, 'tick_size', $defaultPricePrecision),
                    ),
                    'limits' => array(
                        'leverage' => array(
                            'min' => null,
                            'max' => null,
                        ),
                        'amount' => array(
                            'min' => $this->safe_number($market, 'minimum_amount'),
                            'max' => $this->safe_number($market, 'maximum_amount'),
                        ),
                        'price' => array(
                            'min' => $this->safe_number($market, 'minimum_price'),
                            'max' => $this->safe_number($market, 'maximum_price'),
                        ),
                        'cost' => array(
                            'min' => $this->safe_number($market, 'minimum_value'),
                            'max' => $this->safe_number($market, 'maximum_value'),
                        ),
                    ),
                    'info' => $market,
                ), $fee);
            }
            return $result;
        }) ();
    }

    public function parse_balance($response) {
        $payload = $this->safe_value($response, 'payload', array());
        $balances = $this->safe_value($payload, 'balances', array());
        $result = array(
            'info' => $response,
            'timestamp' => null,
            'datetime' => null,
        );
        for ($i = 0; $i < count($balances); $i++) {
            $balance = $balances[$i];
            $currencyId = $this->safe_string($balance, 'currency');
            $code = $this->safe_currency_code($currencyId);
            $account = $this->account();
            $account['free'] = $this->safe_string($balance, 'available');
            $account['used'] = $this->safe_string($balance, 'locked');
            $account['total'] = $this->safe_string($balance, 'total');
            $result[$code] = $account;
        }
        return $this->safe_balance($result);
    }

