    public function parse_trade($trade, $market = null) {
        //
        // fetchTrades (public)
        //
        //      {
        //          "book" => "btc_usdt",
        //          "created_at" => "2021-11-24T12:14:53+0000",
        //          "amount" => "0.00026562",
        //          "maker_side" => "sell",
        //          "price" => "56471.55",
        //          "tid" => "52557338"
        //      }
        //
        // fetchMyTrades (private)
        //
        //      {
        //          "book" => "btc_usdt",
        //          "created_at" => "2021-11-24T12:31:03+0000",
        //          "minor" => "11.30356000",
        //          "major" => "-0.00020000",
        //          "fees_amount" => "0.01119052",
        //          "fees_currency" => "usdt",
        //          "minor_currency" => "usdt",
        //          "major_currency" => "btc",
        //          "oid" => "djTzMIWx2Vi3iMjl",
        //          "tid" => "52559051",
        //          "price" => "56517.80",
        //          "side" => "sell",
        //          "maker_side" => "buy"
        //      }
        //
        // fetchOrderTrades (private)
        //
        //      {
        //          "book" => "btc_usdt",
        //          "created_at" => "2021-11-24T12:30:52+0000",
        //          "minor" => "-11.33047916",
        //          "major" => "0.00020020",
        //          "fees_amount" => "0.00000020",
        //          "fees_currency" => "btc",
        //          "minor_currency" => "usdt",
        //          "major_currency" => "btc",
        //          "oid" => "O0D2zcljjjQF5xlG",
        //          "tid" => "52559030",
        //          "price" => "56595.80",
        //          "side" => "buy",
        //          "maker_side" => "sell"
        //      }
        //
        $timestamp = $this->parse8601($this->safe_string($trade, 'created_at'));
        $marketId = $this->safe_string($trade, 'book');
        $symbol = $this->safe_symbol($marketId, $market, '_');
        $side = $this->safe_string_2($trade, 'side', 'maker_side');
        $makerSide = $this->safe_string($trade, 'maker_side');
        $takerOrMaker = null;
        if ($side === $makerSide) {
            $takerOrMaker = 'maker';
        } else {
            $takerOrMaker = 'taker';
        }
        $amount = $this->safe_string_2($trade, 'amount', 'major');
        if ($amount !== null) {
            $amount = Precise::string_abs($amount);
        }
        $fee = null;
        $feeCost = $this->safe_string($trade, 'fees_amount');
        if ($feeCost !== null) {
            $feeCurrencyId = $this->safe_string($trade, 'fees_currency');
            $feeCurrency = $this->safe_currency_code($feeCurrencyId);
            $fee = array(
                'cost' => $feeCost,
                'currency' => $feeCurrency,
            );
        }
        $cost = $this->safe_string($trade, 'minor');
        if ($cost !== null) {
            $cost = Precise::string_abs($cost);
        }
        $price = $this->safe_string($trade, 'price');
        $orderId = $this->safe_string($trade, 'oid');
        $id = $this->safe_string($trade, 'tid');
        return $this->safe_trade(array(
            'id' => $id,
            'info' => $trade,
            'timestamp' => $timestamp,
            'datetime' => $this->iso8601($timestamp),
            'symbol' => $symbol,
            'order' => $orderId,
            'type' => null,
            'side' => $side,
            'takerOrMaker' => $takerOrMaker,
            'price' => $price,
            'amount' => $amount,
            'cost' => $cost,
            'fee' => $fee,
        ), $market);
    }

