    public function parse_market($market): array {
        $id = $this->safe_string($market, 'name');
        $numericId = $this->safe_integer($market, 'id');
        $parts = explode('_', $id);
        $baseId = $this->safe_string($parts, 0);
        $quoteId = $this->safe_string($parts, 1);
        $base = $this->safe_currency_code($baseId);
        $quote = $this->safe_currency_code($quoteId);
        $active = $this->safe_value($market, 'active');
        $amountPrecision = $this->safe_integer($market, 'baseStep');
        $pricePrecision = $this->safe_integer($market, 'quoteStep');
        $timeStart = $this->safe_string($market, 'timeStart');
        $created = $this->parse8601($timeStart);
        $minCost = null;
        $currenciesValuedInUsd = $this->safe_value($this->options, 'currenciesValuedInUsd', array());
        $quoteInUsd = $this->safe_bool($currenciesValuedInUsd, $quote, false);
        if ($quoteInUsd) {
            $settings = $this->safe_value($market, 'settings', array());
            $minCost = $this->safe_number($settings, 'limit_usd');
        }
        return $this->safe_market_structure(array(
            'id' => $id,
            'numericId' => $numericId,
            'symbol' => $base . '/' . $quote,
            'base' => $base,
            'quote' => $quote,
            'settle' => null,
            'baseId' => $baseId,
            'quoteId' => $quoteId,
            'settleId' => null,
            'type' => 'spot',
            'spot' => true,
            'margin' => false,
            'swap' => false,
            'future' => false,
            'option' => false,
            'active' => $active,
            'contract' => false,
            'linear' => null,
            'inverse' => null,
            'contractSize' => null,
            'expiry' => null,
            'expiryDatetime' => null,
            'strike' => null,
            'optionType' => null,
            'precision' => array(
                'amount' => $amountPrecision,
                'price' => $pricePrecision,
            ),
            'limits' => array(
                'leverage' => array(
                    'min' => null,
                    'max' => null,
                ),
                'amount' => array(
                    'min' => null,
                    'max' => null,
                ),
                'price' => array(
                    'min' => null,
                    'max' => null,
                ),
                'cost' => array(
                    'min' => $minCost,
                    'max' => null,
                ),
            ),
            'created' => $created,
            'info' => $market,
        ));
    }

