            //             "baseDecimals" => 8,
            //             "quoteDecimals" => 6,
            //             "pairId" => 22,
            //             "type" => "limit",
            //             "stopPrice" => null,
            //             "slippage" => null,
            //             "timestamp" => "1700594959"
            //         }
            //     }
            //
            $order = $this->safe_dict($response, 'result', array());
            return $this->parse_order($order, $market);
        }) ();
    }

