        //
        $type = null;
        $id = null;
        $withdrawalId = $this->safe_string($transaction, 'withdrawId');
        $depositId = $this->safe_string($transaction, 'depositId');
        $addressTo = $this->safe_string($transaction, 'address');
        $address = $addressTo;
