            //           ),
            //           ...
            //           message => 'Success',
            //     }
            //
            $data = $this->safe_value($response, 'data', array());
            $coins = is_array($data) ? array_keys($data) : array();
            $result = array();
            for ($i = 0; $i < count($coins); $i++) {
                $coin = $coins[$i];
