    public function parse_leverage_tiers($response, $symbols = null, $marketIdKey = null) {
        //
        //     {
        //         "BTCUSD" => [
        //             ["500001", "100", "0.005"],
        //             ["1000001", "50", "0.01"],
        //             ["2000001", "30", "0.015"],
