        $execInst = $this->safe_value($order, 'exec_inst');
        $postOnly = null;
        if ($execInst !== null) {
            $postOnly = false;
            for ($i = 0; $i < count($execInst); $i++) {
                $inst = $execInst[$i];
                if ($inst === 'POST_ONLY') {
                    $postOnly = true;
                    break;
                }
            }
