            $until = $this->safe_integer($params, 'until');
            $params = $this->omit($params, array( 'until' ));
            if ($until !== null) {
                $request['end_time'] = $until;
            }
            $response = Async\await($this->v1PrivatePostPrivateGetTransactions ($this->extend($request, $params)));
            //
            //     {
