            //                     "event_timestamp_ms" => 1686804665013,
            //                     "event_timestamp_ns" => "1686804665013642422",
            //                     "client_oid" => "CCXT_d6ea7c5db6c1495aa8b758",
            //                     "taker_side" => "",
