    public function fetch_status($params = array ()) {
        return Async\async(function () use ($params) {
            /**
             * the latest known information on the availability of the exchange API
             *
             * @see https://api-docs.defx.com/#4b03bb3b-a0fa-4dfb-b96c-237bde0ce9e6
             *
             * @param {array} [$params] extra parameters specific to the exchange API endpoint
             * @return {array} a ~@link https://docs.ccxt.com/#/?id=exchange-$status-structure $status structure~
             */
            $response = Async\await($this->v1PublicGetHealthcheckPing ($params));
            //
            // {
            //     "success" => true,
            //     "t" => 1709705048323,
            //     "v" => "0.0.7",
            //     "msg" => "A programmer’s wife tells him, “While you’re at the grocery store, buy some eggs.” He never comes back."
            // }
            //
            $status = null;
            $success = $this->safe_bool($response, 'success');
            if ($success) {
                $status = 'ok';
            } else {
                $status = 'error';
            }
            return array(
                'status' => $status,
                'updated' => null,
                'eta' => null,
                'url' => null,
                'info' => $response,
            );
        }) ();
    }

