        //     "bestAskQty" => "10.000",
        //     "openPrice" => "0.00",
        //     "highPrice" => "0.00",
        //     "lowPrice" => "0.00",
        //     "volume" => "0.000",
        //     "quoteVolume" => "0.00",
        //     "openTime" => 1700142658697,
        //     "closeTime" => 1700142658697,
