    public function fetch_ledger(?string $code = null, ?int $since = null, ?int $limit = null, $params = array ()): PromiseInterface {
        return Async\async(function () use ($code, $since, $limit, $params) {
            /**
             * fetch the history of changes, actions done by the user or operations that altered the balance of the user
             *
             * @see https://api-docs.defx.com/#38cc8974-794f-48c0-b959-db045a0ee565
             *
             * @param {string} [$code] unified currency $code
             * @param {int} [$since] timestamp in ms of the earliest ledger entry
             * @param {int} [$limit] max number of ledger entries to return
             * @param {array} [$params] extra parameters specific to the exchange API endpoint
             * @param {int} [$params->until] timestamp in ms of the latest ledger entry
             * @param {boolean} [$params->paginate] default false, when true will automatically $paginate by calling this endpoint multiple times. See in the docs all the [available parameters](https://github.com/ccxt/ccxt/wiki/Manual#pagination-$params)
             * @return {array} a ~@link https://docs.ccxt.com/#/?id=ledger ledger structure~
             */
            Async\await($this->load_markets());
            $paginate = false;
            list($paginate, $params) = $this->handle_option_and_params($params, 'fetchLedger', 'paginate');
            if ($paginate) {
                return Async\await($this->fetch_paginated_call_dynamic('fetchLedger', $code, $since, $limit, $params));
            }
            $request = array();
            if ($since !== null) {
                $request['start'] = $since;
            } else {
                $request['start'] = 0;
            }
            $until = $this->safe_integer($params, 'until');
            if ($until !== null) {
                $params = $this->omit($params, 'until');
                $request['end'] = $until;
            } else {
                $request['end'] = $this->milliseconds();
            }
            $response = Async\await($this->v1PrivateGetApiWalletTransactions ($this->extend($request, $params)));
            $data = $this->safe_list($response, 'transactions', array());
            return $this->parse_ledger($data, null, $since, $limit);
        }) ();
    }

    public function parse_ledger_entry(array $item, ?array $currency = null): array {
        //
        // {
        //     "id" => "01JCSZS6H5VQND3GF5P98SJ29C",
        //     "timestamp" => 1731744012054,
        //     "type" => "FundingFee",
        //     "amount" => "0.02189287",
        //     "asset" => "USDC",
        //     "operation" => "CREDIT"
        // }
        //
        $amount = $this->safe_string($item, 'amount');
        $currencyId = $this->safe_string($item, 'asset');
        $code = $this->safe_currency_code($currencyId, $currency);
        $currency = $this->safe_currency($currencyId, $currency);
        $timestamp = $this->safe_integer($item, 'timestamp');
        $type = $this->safe_string($item, 'type');
        return $this->safe_ledger_entry(array(
            'info' => $item,
            'id' => $this->safe_string($item, 'id'),
            'direction' => null,
            'account' => null,
            'referenceAccount' => null,
            'referenceId' => null,
            'type' => $this->parse_ledger_entry_type($type),
            'currency' => $code,
            'amount' => $this->parse_number($amount),
            'timestamp' => $timestamp,
            'datetime' => $this->iso8601($timestamp),
            'before' => null,
            'after' => null,
            'status' => null,
            'fee' => null,
        ), $currency);
    }

    public function parse_ledger_entry_type($type) {
        $ledgerType = array(
            'FundingFee' => 'fee',
            'FeeRebate' => 'fee',
            'FeeKickback' => 'fee',
            'RealizedPnl' => 'trade',
            'LiquidationClearance' => 'trade',
            'Transfer' => 'transfer',
            'ReferralPayout' => 'referral',
            'Commission' => 'commission',
        );
        return $this->safe_string($ledgerType, $type, $type);
    }

