            Async\await($this->load_markets());
            $request = array(
                'leverage' => $this->number_to_string($leverage),
            );
            $market = $this->market($symbol);
            $request['symbol'] = $market['id'];
            $response = Async\await($this->v1PrivatePostApiUsersMetadataLeverage ($this->extend($request, $params)));
            //
            // {
            //     "success" => true,
            //     "data" => {
