            Async\await($this->load_markets());
            $code = null;
            if ($symbols === null) {
                $code = $this->code_from_options('fetchPositions', $params);
            } elseif (gettype($symbols) === 'string') {
                $code = $symbols;
                $symbols = null; // fix https://github.com/ccxt/ccxt/issues/13961
