            //         value_disp => "10.00000000",
            //         value_xint => array(
            //           v => "1000000000",
            //           e => "8",
            //           f => "10",
            //         ),
            //         display => "10.00000000DOGE",
            //         display_short => "10.00000000DOGE",
            //         $currency => "DOGE",
