             * fetch all unfilled currently open $orders
             * @param {string|null} $symbol unified $market $symbol
             * @param {int|null} $since the earliest time in ms to fetch open $orders for
             * @param {int|null} $limit the maximum number of  open $orders structures to retrieve
             * @param {array} $params extra parameters specific to the exmo api endpoint
             * @return {[array]} a list of ~@link https://docs.ccxt.com/#/?id=order-structure order structures~
             */
            Async\await($this->load_markets());
            if ($symbol !== null) {
                $market = $this->market($symbol);
                $symbol = $market['symbol'];
            }
            $response = Async\await($this->privatePostUserOpenOrders ($params));
            $marketIds = is_array($response) ? array_keys($response) : array();
            $orders = array();
            for ($i = 0; $i < count($marketIds); $i++) {
                $marketId = $marketIds[$i];
                $market = $this->safe_market($marketId);
                $params = array_merge($params, array(
                    'status' => 'open',
                ));
                $parsedOrders = $this->parse_orders($response[$marketId], $market, $since, $limit, $params);
                $orders = $this->array_concat($orders, $parsedOrders);
            }
            return $this->filter_by_symbol_since_limit($orders, $symbol, $since, $limit);
        }) ();
    }

    public function parse_order($order, $market = null) {
        //
        // fetchOrders, fetchOpenOrders, fetchClosedOrders, fetchCanceledOrders
        //
        //     {
        //         "order_id" => "14",
        //         "created" => "1435517311",
        //         "type" => "buy",
        //         "pair" => "BTC_USD",
        //         "price" => "100",
        //         "quantity" => "1",
        //         "amount" => "100"
        //     }
        //
        // fetchOrder
        //
        //     {
        //         "type" => "buy",
        //         "in_currency" => "BTC",
        //         "in_amount" => "1",
        //         "out_currency" => "USD",
        //         "out_amount" => "100",
        //         "trades" => array(
        //             {
        //                 "trade_id" => 3,
        //                 "date" => 1435488248,
        //                 "type" => "buy",
        //                 "pair" => "BTC_USD",
        //                 "order_id" => 12345,
        //                 "quantity" => 1,
        //                 "price" => 100,
        //                 "amount" => 100
        //             }
        //         )
        //     }
        //
        $id = $this->safe_string($order, 'order_id');
        $timestamp = $this->safe_timestamp($order, 'created');
        $side = $this->safe_string($order, 'type');
        $marketId = null;
        if (is_array($order) && array_key_exists('pair', $order)) {
            $marketId = $order['pair'];
        } elseif ((is_array($order) && array_key_exists('in_currency', $order)) && (is_array($order) && array_key_exists('out_currency', $order))) {
            if ($side === 'buy') {
                $marketId = $order['in_currency'] . '_' . $order['out_currency'];
            } else {
                $marketId = $order['out_currency'] . '_' . $order['in_currency'];
            }
        }
        $market = $this->safe_market($marketId, $market);
        $symbol = $market['symbol'];
        $amount = $this->safe_string($order, 'quantity');
        if ($amount === null) {
            $amountField = ($side === 'buy') ? 'in_amount' : 'out_amount';
            $amount = $this->safe_string($order, $amountField);
        }
        $price = $this->safe_string($order, 'price');
        $cost = $this->safe_string($order, 'amount');
        $transactions = $this->safe_value($order, 'trades', array());
        $clientOrderId = $this->safe_integer($order, 'client_id');
        return $this->safe_order(array(
            'id' => $id,
            'clientOrderId' => $clientOrderId,
            'datetime' => $this->iso8601($timestamp),
            'timestamp' => $timestamp,
            'lastTradeTimestamp' => null,
            'status' => null,
            'symbol' => $symbol,
            'type' => 'limit',
            'timeInForce' => null,
            'postOnly' => null,
            'side' => $side,
            'price' => $price,
            'stopPrice' => null,
            'triggerPrice' => null,
            'cost' => $cost,
            'amount' => $amount,
            'filled' => null,
            'remaining' => null,
            'average' => null,
            'trades' => $transactions,
            'fee' => null,
            'info' => $order,
        ), $market);
    }

