        //         "type" => "order"
        //     }
        //
        //     {
        //         "baseCurrency" => "BTC",
        //         "$fee" => 0,
        //         "feeCurrency" => "USD",
        //         "feeRate" => 0,
        //         "future" => null,
        //         "$id" => 664079556,
        //         "liquidity" => "taker",
        //         "$market" => null,
        //         "$orderId" => null,
        //         "$price" => 34830.61359,
        //         "quoteCurrency" => "USD",
        //         "$side" => "sell",
        //         "size" => 0.0005996,
        //         "time" => "2021-01-15T16:05:29.246135+00:00",
        //         "tradeId" => null,
        //         "type" => "otc"
        //     }
        //
        //     with -ve $fee
        //     {
        //         "$id" => 1171258927,
        //         "$fee" => -0.0000713875,
        //         "$side" => "sell",
        //         "size" => 1,
        //         "time" => "2021-03-11T13:34:35.523627+00:00",
        //         "type" => "order",
        //         "$price" => 14.2775,
        //         "future" => null,
        //         "$market" => "SOL/USD",
        //         "feeRate" => -0.000005,
        //         "$orderId" => 33182929044,
        //         "tradeId" => 582936801,
        //         "liquidity" => "maker",
        //         "feeCurrency" => "USD",
        //         "baseCurrency" => "SOL",
        //         "quoteCurrency" => "USD"
        //     }
        //
        //     // from OTC order
        //     {
        //         "$id" => 1172129651,
        //         "$fee" => 0,
        //         "$side" => "sell",
        //         "size" => 1.47568846,
        //         "time" => "2021-03-11T15:04:46.893383+00:00",
        //         "type" => "otc",
        //         "$price" => 14.60932598,
        //         "future" => null,
        //         "$market" => null,
        //         "feeRate" => 0,
        //         "$orderId" => null,
        //         "tradeId" => null,
        //         "liquidity" => "taker",
        //         "feeCurrency" => "USD",
        //         "baseCurrency" => "BCHA",
        //         "quoteCurrency" => "USD"
        //     }
        $id = $this->safe_string($trade, 'id');
        $takerOrMaker = $this->safe_string($trade, 'liquidity');
        $marketId = $this->safe_string($trade, 'market');
        $symbol = null;
        if (is_array($this->markets_by_id) && array_key_exists($marketId, $this->markets_by_id)) {
            $market = $this->markets_by_id[$marketId];
            $symbol = $market['symbol'];
        } else {
            $base = $this->safe_currency_code($this->safe_string($trade, 'baseCurrency'));
            $quote = $this->safe_currency_code($this->safe_string($trade, 'quoteCurrency'));
            if (($base !== null) && ($quote !== null)) {
                $symbol = $base . '/' . $quote;
            } else {
                $symbol = $marketId;
            }
        }
        $timestamp = $this->parse8601($this->safe_string($trade, 'time'));
        $priceString = $this->safe_string($trade, 'price');
        $amountString = $this->safe_string($trade, 'size');
        $price = $this->parse_number($priceString);
        $amount = $this->parse_number($amountString);
        $cost = $this->parse_number(Precise::string_mul($priceString, $amountString));
        if (($symbol === null) && ($market !== null)) {
            $symbol = $market['symbol'];
        }
        $side = $this->safe_string($trade, 'side');
        $fee = null;
        $feeCost = $this->safe_number($trade, 'fee');
        if ($feeCost !== null) {
            $feeCurrencyId = $this->safe_string($trade, 'feeCurrency');
            $feeCurrencyCode = $this->safe_currency_code($feeCurrencyId);
            $fee = array(
                'cost' => $feeCost,
                'currency' => $feeCurrencyCode,
                'rate' => $this->safe_number($trade, 'feeRate'),
            );
        }
        $orderId = $this->safe_string($trade, 'orderId');
        return array(
            'info' => $trade,
            'timestamp' => $timestamp,
            'datetime' => $this->iso8601($timestamp),
            'symbol' => $symbol,
            'id' => $id,
            'order' => $orderId,
            'type' => null,
            'takerOrMaker' => $takerOrMaker,
            'side' => $side,
            'price' => $price,
            'amount' => $amount,
            'cost' => $cost,
            'fee' => $fee,
        );
    }

