    public function parse_trade($trade, $market = null) {
        //
        // public
        //     {
        //       "$id" => "1334253759",
        //       "create_time" => "1626342738",
        //       "create_time_ms" => "1626342738331.497000",
        //       "currency_pair" => "BTC_USDT",
        //       "$side" => "sell",
        //       "$amount" => "0.0022",
        //       "$price" => "32452.16"
        //     }
        //
        // private
        //     {
        //       "$id" => "218087755",
        //       "create_time" => "1578958740",
        //       "create_time_ms" => "1578958740122.710000",
        //       "currency_pair" => "BTC_USDT",
        //       "$side" => "sell",
        //       "role" => "taker",
        //       "$amount" => "0.0004",
        //       "$price" => "8112.77",
        //       "order_id" => "8445563839",
        //       "$fee" => "0.006490216",
        //       "fee_currency" => "USDT",
        //       "point_fee" => "0",
        //       "gt_fee" => "0"
        //     }
        //
        $id = $this->safe_string($trade, 'id');
        $timestampString = $this->safe_string_2($trade, 'create_time_ms', 'time');
        $timestamp = null;
        if (mb_strpos($timestampString, '.') > 0) {
            $milliseconds = explode('.', $timestampString);
            $timestamp = intval($milliseconds[0]);
        }
        $marketId = $this->safe_string($trade, 'currency_pair');
        $symbol = $this->safe_symbol($marketId, $market);
        $amountString = $this->safe_string($trade, 'amount');
        $priceString = $this->safe_string($trade, 'price');
        $cost = $this->parse_number(Precise::string_mul($amountString, $priceString));
        $amount = $this->parse_number($amountString);
        $price = $this->parse_number($priceString);
        $side = $this->safe_string($trade, 'side');
        $orderId = $this->safe_string($trade, 'order_id');
        $gtFee = $this->safe_string($trade, 'gt_fee');
        $feeCurrency = null;
        $feeCost = null;
        if ($gtFee === '0') {
            $feeCurrency = $this->safe_string($trade, 'fee_currency');
            $feeCost = $this->safe_number($trade, 'fee');
        } else {
            $feeCurrency = 'GT';
            $feeCost = $this->parse_number($gtFee);
        }
        $fee = array(
            'cost' => $feeCost,
            'currency' => $feeCurrency,
        );
        $takerOrMaker = $this->safe_string($trade, 'role');
        return array(
            'info' => $trade,
            'id' => $id,
            'timestamp' => $timestamp,
            'datetime' => $this->iso8601($timestamp),
            'symbol' => $symbol,
            'order' => $orderId,
            'type' => null,
            'side' => $side,
            'takerOrMaker' => $takerOrMaker,
            'price' => $price,
            'amount' => $amount,
            'cost' => $cost,
            'fee' => $fee,
        );
    }

