    public function parse_balance($response) {
        $result = array( 'info' => $response );
        for ($i = 0; $i < count($response); $i++) {
            $entry = $response[$i];
            $currencyId = $this->safe_string($entry, 'currency');
            $code = $this->safe_currency_code($currencyId);
            $account = $this->account();
            $account['free'] = $this->safe_string($entry, 'available');
            $account['used'] = $this->safe_string($entry, 'reserved');
            $result[$code] = $account;
        }
        return $this->safe_balance($result);
    }

    public function fetch_balance($params = array ()) {
        return Async\async(function () use ($params) {
            /**
             * query for balance and get the amount of funds available for trading or funds locked in orders
             * @param {array} $params extra parameters specific to the hitbtc3 api endpoint
             * @return {array} a ~@link https://docs.ccxt.com/en/latest/manual.html?#balance-structure balance structure~
             */
            $type = $this->safe_string_lower($params, 'type', 'spot');
            $params = $this->omit($params, array( 'type' ));
            $accountsByType = $this->safe_value($this->options, 'accountsByType', array());
            $account = $this->safe_string($accountsByType, $type, $type);
            $response = null;
            if ($account === 'wallet') {
                $response = Async\await($this->privateGetWalletBalance ($params));
            } elseif ($account === 'spot') {
                $response = Async\await($this->privateGetSpotBalance ($params));
            } elseif ($account === 'derivatives') {
                $response = Async\await($this->privateGetFuturesBalance ($params));
            } else {
                $keys = is_array($accountsByType) ? array_keys($accountsByType) : array();
                throw new BadRequest($this->id . ' fetchBalance() $type parameter must be one of ' . implode(', ', $keys));
            }
            //
            //     array(
            //       array(
            //         "currency" => "PAXG",
            //         "available" => "0",
            //         "reserved" => "0",
            //         "reserved_margin" => "0",
            //       ),
            //       ...
            //     )
            //
            return $this->parse_balance($response);
        }) ();
    }

    public function fetch_ticker($symbol, $params = array ()) {
        return Async\async(function () use ($symbol, $params) {
            /**
             * fetches a price ticker, a statistical calculation with the information calculated over the past 24 hours for a specific market
             * @param {string} $symbol unified $symbol of the market to fetch the ticker for
             * @param {array} $params extra parameters specific to the hitbtc3 api endpoint
             * @return {array} a {@link https://docs.ccxt.com/en/latest/manual.html#ticker-structure ticker structure}
             */
            $response = Async\await($this->fetch_tickers(array( $symbol ), $params));
            return $this->safe_value($response, $symbol);
        }) ();
    }

    public function fetch_tickers($symbols = null, $params = array ()) {
        return Async\async(function () use ($symbols, $params) {
            /**
             * fetches price tickers for multiple markets, statistical calculations with the information calculated over the past 24 hours each $market
             * @param {[string]|null} $symbols unified $symbols of the markets to fetch the ticker for, all $market tickers are returned if not assigned
             * @param {array} $params extra parameters specific to the hitbtc3 api endpoint
             * @return {array} an array of {@link https://docs.ccxt.com/en/latest/manual.html#ticker-structure ticker structures}
             */
            Async\await($this->load_markets());
            $symbols = $this->market_symbols($symbols);
            $request = array();
            if ($symbols !== null) {
                $marketIds = $this->market_ids($symbols);
                $delimited = implode(',', $marketIds);
                $request['symbols'] = $delimited;
            }
            $response = Async\await($this->publicGetPublicTicker (array_merge($request, $params)));
            //
            //     {
            //       "BTCUSDT" => {
            //         "ask" => "63049.06",
            //         "bid" => "63046.41",
            //         "last" => "63048.36",
            //         "low" => "62010.00",
            //         "high" => "66657.99",
            //         "open" => "64839.75",
            //         "volume" => "15272.13278",
            //         "volume_quote" => "976312127.6277998",
            //         "timestamp" => "2021-10-22T04:25:47.573Z"
            //       }
            //     }
            //
            $result = array();
            $keys = is_array($response) ? array_keys($response) : array();
            for ($i = 0; $i < count($keys); $i++) {
                $marketId = $keys[$i];
                $market = $this->safe_market($marketId);
                $symbol = $market['symbol'];
                $entry = $response[$marketId];
                $result[$symbol] = $this->parse_ticker($entry, $market);
            }
            return $this->filter_by_array($result, 'symbol', $symbols);
        }) ();
    }

    public function parse_ticker($ticker, $market = null) {
        //
        //     {
        //       "ask" => "62756.01",
        //       "bid" => "62754.09",
        //       "last" => "62755.87",
        //       "low" => "62010.00",
        //       "high" => "66657.99",
        //       "open" => "65089.27",
        //       "volume" => "16719.50366",
        //       "volume_quote" => "1063422878.8156828",
        //       "timestamp" => "2021-10-22T07:29:14.585Z"
        //     }
        //
        $timestamp = $this->parse8601($ticker['timestamp']);
        $symbol = $this->safe_symbol(null, $market);
        $baseVolume = $this->safe_string($ticker, 'volume');
        $quoteVolume = $this->safe_string($ticker, 'volume_quote');
        $open = $this->safe_string($ticker, 'open');
        $last = $this->safe_string($ticker, 'last');
        return $this->safe_ticker(array(
            'symbol' => $symbol,
            'timestamp' => $timestamp,
            'datetime' => $this->iso8601($timestamp),
            'high' => $this->safe_string($ticker, 'high'),
            'low' => $this->safe_string($ticker, 'low'),
            'bid' => $this->safe_string($ticker, 'bid'),
            'bidVolume' => null,
            'ask' => $this->safe_string($ticker, 'ask'),
            'askVolume' => null,
            'vwap' => null,
            'open' => $open,
            'close' => $last,
            'last' => $last,
            'previousClose' => null,
            'change' => null,
            'percentage' => null,
            'average' => null,
            'baseVolume' => $baseVolume,
            'quoteVolume' => $quoteVolume,
            'info' => $ticker,
        ), $market);
    }

    public function fetch_trades($symbol, $since = null, $limit = null, $params = array ()) {
        return Async\async(function () use ($symbol, $since, $limit, $params) {
            /**
             * get the list of most recent $trades for a particular $symbol
             * @param {string} $symbol unified $symbol of the $market to fetch $trades for
             * @param {int|null} $since timestamp in ms of the earliest trade to fetch
             * @param {int|null} $limit the maximum amount of $trades to fetch
             * @param {array} $params extra parameters specific to the hitbtc3 api endpoint
             * @return {[array]} a list of ~@link https://docs.ccxt.com/en/latest/manual.html?#public-$trades trade structures~
             */
            Async\await($this->load_markets());
            $market = null;
            $request = array();
            if ($symbol !== null) {
                $market = $this->market($symbol);
                // $symbol is optional for hitbtc fetchTrades
                $request['symbols'] = $market['id'];
            }
            if ($limit !== null) {
                $request['limit'] = $limit;
            }
            if ($since !== null) {
                $request['from'] = $since;
            }
            $response = Async\await($this->publicGetPublicTrades (array_merge($request, $params)));
            $marketIds = is_array($response) ? array_keys($response) : array();
            $trades = array();
            for ($i = 0; $i < count($marketIds); $i++) {
                $marketId = $marketIds[$i];
                $market = $this->market($marketId);
                $rawTrades = $response[$marketId];
                $parsed = $this->parse_trades($rawTrades, $market);
                $trades = $this->array_concat($trades, $parsed);
            }
            return $trades;
        }) ();
    }

    public function fetch_my_trades($symbol = null, $since = null, $limit = null, $params = array ()) {
        return Async\async(function () use ($symbol, $since, $limit, $params) {
            /**
             * fetch all trades made by the user
             * @param {string|null} $symbol unified $market $symbol
             * @param {int|null} $since the earliest time in ms to fetch trades for
             * @param {int|null} $limit the maximum number of trades structures to retrieve
             * @param {array} $params extra parameters specific to the hitbtc3 api endpoint
             * @param {string|null} $params->marginMode 'cross' or 'isolated' only 'isolated' is supported
             * @param {bool|null} $params->margin true for fetching margin trades
             * @return {[array]} a list of {@link https://docs.ccxt.com/en/latest/manual.html#trade-structure trade structures}
             */
            Async\await($this->load_markets());
            $market = null;
            $request = array();
            if ($symbol !== null) {
                $market = $this->market($symbol);
                $request['symbol'] = $market['id'];
            }
            if ($limit !== null) {
                $request['limit'] = $limit;
            }
            if ($since !== null) {
                $request['from'] = $since;
            }
            $marketType = null;
            list($marketType, $params) = $this->handle_market_type_and_params('fetchMyTrades', $market, $params);
            $method = $this->get_supported_mapping($marketType, array(
                'spot' => 'privateGetSpotHistoryTrade',
                'swap' => 'privateGetFuturesHistoryTrade',
                'margin' => 'privateGetMarginHistoryTrade',
            ));
            list($marginMode, $query) = $this->handle_margin_mode_and_params('fetchMyTrades', $params);
            if ($marginMode !== null) {
                $method = 'privateGetMarginHistoryTrade';
            }
            $response = Async\await($this->$method (array_merge($request, $query)));
            return $this->parse_trades($response, $market, $since, $limit);
        }) ();
    }

    public function parse_trade($trade, $market = null) {
        //
        // createOrder ($market)
        //
        //  {
        //      $id => '1569252895',
        //      position_id => '0',
        //      quantity => '10',
        //      price => '0.03919424',
        //      $fee => '0.000979856000',
        //      $timestamp => '2022-01-25T19:38:36.153Z',
        //      $taker => true
        //  }
        //
        // fetchTrades
        //
        //  {
        //      $id => 974786185,
        //      price => '0.032462',
        //      qty => '0.3673',
        //      $side => 'buy',
        //      $timestamp => '2020-10-16T12:57:39.846Z'
        //  }
        //
        // fetchMyTrades spot
        //
        //  {
        //      $id => 277210397,
        //      clientOrderId => '6e102f3e7f3f4e04aeeb1cdc95592f1a',
        //      $orderId => 28102855393,
        //      $symbol => 'ETHBTC',
        //      $side => 'sell',
        //      quantity => '0.002',
        //      price => '0.073365',
        //      $fee => '0.000000147',
        //      $timestamp => '2018-04-28T18:39:55.345Z',
        //      $taker => true
        //  }
        //
        // fetchMyTrades swap and margin
        //
        //  {
        //      "id" => 4718564,
        //      "order_id" => 58730811958,
        //      "client_order_id" => "475c47d97f867f09726186eb22b4c3d4",
        //      "symbol" => "BTCUSDT_PERP",
        //      "side" => "sell",
        //      "quantity" => "0.0001",
        //      "price" => "41118.51",
        //      "fee" => "0.002055925500",
        //      "timestamp" => "2022-03-17T05:23:17.795Z",
        //      "taker" => true,
        //      "position_id" => 2350122,
        //      "pnl" => "0.002255000000",
        //      "liquidation" => false
        //  }
        //
        $timestamp = $this->parse8601($trade['timestamp']);
        $marketId = $this->safe_string($trade, 'symbol');
        $market = $this->safe_market($marketId, $market);
        $symbol = $market['symbol'];
        $fee = null;
        $feeCostString = $this->safe_string($trade, 'fee');
        $taker = $this->safe_value($trade, 'taker');
        $takerOrMaker = null;
        if ($taker !== null) {
            $takerOrMaker = $taker ? 'taker' : 'maker';
        }
        if ($feeCostString !== null) {
            $info = $this->safe_value($market, 'info', array());
            $feeCurrency = $this->safe_string($info, 'fee_currency');
            $feeCurrencyCode = $this->safe_currency_code($feeCurrency);
            $fee = array(
                'cost' => $feeCostString,
                'currency' => $feeCurrencyCode,
            );
        }
        // we use clientOrderId as the order $id with this exchange intentionally
        // because most of their endpoints will require clientOrderId
        // explained here => https://github.com/ccxt/ccxt/issues/5674
        $orderId = $this->safe_string_2($trade, 'clientOrderId', 'client_order_id');
        $priceString = $this->safe_string($trade, 'price');
        $amountString = $this->safe_string_2($trade, 'quantity', 'qty');
        $side = $this->safe_string($trade, 'side');
        $id = $this->safe_string($trade, 'id');
        return $this->safe_trade(array(
            'info' => $trade,
            'id' => $id,
            'order' => $orderId,
            'timestamp' => $timestamp,
            'datetime' => $this->iso8601($timestamp),
            'symbol' => $symbol,
            'type' => null,
            'side' => $side,
            'takerOrMaker' => $takerOrMaker,
            'price' => $priceString,
            'amount' => $amountString,
            'cost' => null,
            'fee' => $fee,
        ), $market);
    }

