            //     {
            //         "BTCUSDT_PERP" => array(
            //             array(
            //                 "timestamp" => "2022-04-01T01:28:00.000Z",
            //                 "open" => "45146.39",
            //                 "close" => "45219.43",
            //                 "min" => "45146.39",
            //                 "max" => "45219.43"
            //             ),
            //         )
            //     }
            //
