    public function fetch_markets($params = array ()) {
        $options = $this->safe_value($this->options, 'fetchMarkets', array());
        $defaultType = $this->safe_string($this->options, 'defaultType', 'spot');
        $type = $this->safe_string($options, 'type', $defaultType);
        $type = $this->safe_string($params, 'type', $type);
        if (($type !== 'spot') && ($type !== 'future') && ($type !== 'swap')) {
            throw new ExchangeError($this->id . " does not support '" . $type . "' $type, set exchange.options['defaultType'] to 'spot', 'future', 'swap'"); // eslint-disable-line quotes
        }
        $method = 'spotPublicGetV1CommonSymbols';
        $query = $this->omit($params, array( 'type', 'subType' ));
        $spot = ($type === 'spot');
        $contract = ($type !== 'spot');
        $future = ($type === 'future');
        $swap = ($type === 'swap');
        $linear = null;
        $inverse = null;
        if ($contract) {
            $defaultSubType = $this->safe_string($this->options, 'defaultSubType', 'inverse');
            $subType = $this->safe_string($options, 'subType', $defaultSubType);
            $subType = $this->safe_string($params, 'subType', $subType);
            if (($subType !== 'inverse') && ($subType !== 'linear')) {
                throw new ExchangeError($this->id . " does not support '" . $subType . "' $type, set exchange.options['defaultSubType'] to 'inverse' or 'linear'"); // eslint-disable-line quotes
            }
            $linear = ($subType === 'linear');
            $inverse = ($subType === 'inverse') || $future;
            if ($future) {
                $method = 'contractPublicGetApiV1ContractContractInfo';
            } else if ($swap) {
                if ($inverse) {
                    $method = 'contractPublicGetSwapApiV1SwapContractInfo';
                } else if ($linear) {
                    $method = 'contractPublicGetLinearSwapApiV1SwapContractInfo';
                }
            }
        }
        $response = yield $this->$method ($query);
        //
        // $spot
        //
        //     {
        //         "status":"ok",
        //         "data":array(
        //             array(
        //                 "base-currency":"xrp3s",
        //                 "quote-currency":"usdt",
        //                 "price-$precision":4,
        //                 "amount-$precision":4,
        //                 "symbol-partition":"innovation",
        //                 "symbol":"xrp3susdt",
        //                 "state":"online",
        //                 "value-$precision":8,
        //                 "min-order-amt":0.01,
        //                 "max-order-amt":1616.4353,
        //                 "min-order-value":5,
        //                 "limit-order-min-order-amt":0.01,
        //                 "limit-order-max-order-amt":1616.4353,
        //                 "limit-order-max-buy-amt":1616.4353,
        //                 "limit-order-max-sell-amt":1616.4353,
        //                 "sell-$market-min-order-amt":0.01,
        //                 "sell-$market-max-order-amt":1616.4353,
        //                 "buy-$market-max-order-value":2500,
        //                 "max-order-value":2500,
        //                 "underlying":"xrpusdt",
        //                 "mgmt-fee-rate":0.035000000000000000,
        //                 "charge-time":"23:55:00",
        //                 "rebal-time":"00:00:00",
        //                 "rebal-threshold":-5,
        //                 "init-nav":10.000000000000000000,
        //                 "api-trading":"enabled",
        //                 "tags":"etp,nav,holdinglimit"
        //             ),
        //         )
        //     }
        //
        // $future
        //
        //     {
        //         "status":"ok",
        //         "data":array(
        //             array(
        //                 "symbol":"BTC",
        //                 "contract_code":"BTC211126",
        //                 "contract_type":"this_week",
        //                 "contract_size":100.000000000000000000,
        //                 "price_tick":0.010000000000000000,
        //                 "delivery_date":"20211126",
        //                 "delivery_time":"1637913600000",
        //                 "create_date":"20211112",
        //                 "contract_status":1,
        //                 "settlement_time":"1637481600000"
        //             ),
        //         ),
        //         "ts":1637474595140
        //     }
        //
        // swaps
        //
        //     {
        //         "status":"ok",
        //         "data":array(
        //             array(
        //                 "symbol":"BTC",
        //                 "contract_code":"BTC-USDT",
        //                 "contract_size":0.001000000000000000,
        //                 "price_tick":0.100000000000000000,
        //                 "delivery_time":"",
        //                 "create_date":"20201021",
        //                 "contract_status":1,
        //                 "settlement_date":"1637481600000",
        //                 "support_margin_mode":"all", // isolated
        //             ),
        //         ),
        //         "ts":1637474774467
        //     }
        //
        $markets = $this->safe_value($response, 'data');
        $numMarkets = is_array($markets) ? count($markets) : 0;
        if ($numMarkets < 1) {
            throw new NetworkError($this->id . ' fetchMarkets() returned an empty $response => ' . $this->json($markets));
        }
        $result = array();
        for ($i = 0; $i < count($markets); $i++) {
            $market = $markets[$i];
            $baseId = null;
            $quoteId = null;
            $settleId = null;
            $id = null;
            if ($contract) {
                $id = $this->safe_string($market, 'contract_code');
                if ($swap) {
                    $parts = explode('-', $id);
                    $baseId = $this->safe_string($market, 'symbol');
                    $quoteId = $this->safe_string($parts, 1);
                    $settleId = $inverse ? $baseId : $quoteId;
                } else if ($future) {
                    $baseId = $this->safe_string($market, 'symbol');
                    $quoteId = 'USD';
                    $settleId = $baseId;
                }
            } else {
                $baseId = $this->safe_string($market, 'base-currency');
                $quoteId = $this->safe_string($market, 'quote-currency');
                $id = $baseId . $quoteId;
            }
            $base = $this->safe_currency_code($baseId);
            $quote = $this->safe_currency_code($quoteId);
            $settle = $this->safe_currency_code($settleId);
            $symbol = $base . '/' . $quote;
            $expiry = null;
            if ($contract) {
                if ($inverse) {
                    $symbol .= ':' . $base;
                } else if ($linear) {
                    $symbol .= ':' . $quote;
                }
                if ($future) {
                    $expiry = $this->safe_integer($market, 'delivery_time');
                    $symbol .= '-' . $this->yymmdd($expiry);
                }
            }
            $contractSize = $this->safe_number($market, 'contract_size');
            $pricePrecision = null;
            $amountPrecision = null;
            $costPrecision = null;
            if ($spot) {
                $pricePrecision = $this->safe_string($market, 'price-precision');
                $pricePrecision = $this->parse_number('1e-' . $pricePrecision);
                $amountPrecision = $this->safe_string($market, 'amount-precision');
                $amountPrecision = $this->parse_number('1e-' . $amountPrecision);
                $costPrecision = $this->safe_string($market, 'value-precision');
                $costPrecision = $this->parse_number('1e-' . $costPrecision);
            } else {
                $pricePrecision = $this->safe_number($market, 'price_tick');
                $amountPrecision = 1;
            }
            $precision = array(
                'amount' => $amountPrecision,
                'price' => $pricePrecision,
                'cost' => $costPrecision,
            );
            $maker = null;
            $taker = null;
            if ($spot) {
                $maker = ($base === 'OMG') ? 0 : 0.2 / 100;
                $taker = ($base === 'OMG') ? 0 : 0.2 / 100;
            }
            $minAmount = $this->safe_number($market, 'min-order-amt', pow(10, -$precision['amount']));
            $maxAmount = $this->safe_number($market, 'max-order-amt');
            $minCost = $this->safe_number($market, 'min-order-value', 0);
            $active = null;
            if ($spot) {
                $state = $this->safe_string($market, 'state');
                $active = ($state === 'online');
            } else if ($contract) {
                $contractStatus = $this->safe_integer($market, 'contract_status');
                $active = ($contractStatus === 1);
            }
            // 0 Delisting
            // 1 Listing
            // 2 Pending Listing
            // 3 Suspension
            // 4 Suspending of Listing
            // 5 In Settlement
            // 6 Delivering
            // 7 Settlement Completed
            // 8 Delivered
            // 9 Suspending of Trade
            $result[] = array(
                'id' => $id,
                'symbol' => $symbol,
                'base' => $base,
                'quote' => $quote,
                'settle' => $settle,
                'baseId' => $baseId,
                'quoteId' => $quoteId,
                'settleId' => $settleId,
                'type' => $type,
                'contract' => $contract,
                'spot' => $spot,
                'future' => $future,
                'swap' => $swap,
                'linear' => $linear,
                'inverse' => $inverse,
                'expiry' => $expiry,
                'expiryDatetime' => $this->iso8601($expiry),
                'contractSize' => $contractSize,
                'active' => $active,
                'precision' => $precision,
                'taker' => $taker,
                'maker' => $maker,
                'limits' => array(
                    'amount' => array(
                        'min' => $minAmount,
                        'max' => $maxAmount,
                    ),
                    'price' => array(
                        'min' => $pricePrecision,
                        'max' => null,
                    ),
                    'cost' => array(
                        'min' => $minCost,
                        'max' => null,
                    ),
                    'leverage' => array(
                        'max' => $this->safe_number($market, 'leverage-ratio', 1),
                        'superMax' => $this->safe_number($market, 'super-margin-leverage-ratio', 1),
                    ),
                ),
                'info' => $market,
            );
        }
        return $result;
    }

    public function parse_ticker($ticker, $market = null) {
        //
        // fetchTicker
        //
        //     {
        //         "amount" => 26228.672978342216,
        //         "open" => 9078.95,
        //         "close" => 9146.86,
        //         "high" => 9155.41,
        //         "id" => 209988544334,
        //         "count" => 265846,
        //         "low" => 8988.0,
        //         "version" => 209988544334,
        //         "ask" => array( 9146.87, 0.156134 ),
        //         "vol" => 2.3822168242201668E8,
        //         "bid" => array( 9146.86, 0.080758 ),
        //     }
        //
        // fetchTickers
        //
        //     {
        //         $symbol => "bhdht",
        //         $open =>  2.3938,
        //         high =>  2.4151,
        //         low =>  2.3323,
        //         $close =>  2.3909,
        //         amount =>  628.992,
        //         vol =>  1493.71841095,
        //         count =>  2088,
        //         $bid =>  2.3643,
        //         bidSize =>  0.7136,
        //         $ask =>  2.4061,
        //         askSize =>  0.4156
        //     }
        //
        $marketId = $this->safe_string_2($ticker, 'symbol', 'contract_code');
        $symbol = $this->safe_symbol($marketId, $market);
        $timestamp = $this->safe_integer($ticker, 'ts');
        $bid = null;
        $bidVolume = null;
        $ask = null;
        $askVolume = null;
        if (is_array($ticker) && array_key_exists('bid', $ticker)) {
            if (gettype($ticker['bid']) === 'array' && count(array_filter(array_keys($ticker['bid']), 'is_string')) == 0) {
                $bid = $this->safe_number($ticker['bid'], 0);
                $bidVolume = $this->safe_number($ticker['bid'], 1);
            } else {
                $bid = $this->safe_number($ticker, 'bid');
                $bidVolume = $this->safe_value($ticker, 'bidSize');
            }
        }
        if (is_array($ticker) && array_key_exists('ask', $ticker)) {
            if (gettype($ticker['ask']) === 'array' && count(array_filter(array_keys($ticker['ask']), 'is_string')) == 0) {
                $ask = $this->safe_number($ticker['ask'], 0);
                $askVolume = $this->safe_number($ticker['ask'], 1);
            } else {
                $ask = $this->safe_number($ticker, 'ask');
                $askVolume = $this->safe_value($ticker, 'askSize');
            }
        }
        $open = $this->safe_number($ticker, 'open');
        $close = $this->safe_number($ticker, 'close');
        $baseVolume = $this->safe_number($ticker, 'amount');
        $quoteVolume = $this->safe_number($ticker, 'vol');
        $vwap = $this->vwap($baseVolume, $quoteVolume);
        return $this->safe_ticker(array(
            'symbol' => $symbol,
            'timestamp' => $timestamp,
            'datetime' => $this->iso8601($timestamp),
            'high' => $this->safe_number($ticker, 'high'),
            'low' => $this->safe_number($ticker, 'low'),
            'bid' => $bid,
            'bidVolume' => $bidVolume,
            'ask' => $ask,
            'askVolume' => $askVolume,
            'vwap' => $vwap,
            'open' => $open,
            'close' => $close,
            'last' => $close,
            'previousClose' => null,
            'change' => null,
            'percentage' => null,
            'average' => null,
            'baseVolume' => $baseVolume,
            'quoteVolume' => $quoteVolume,
            'info' => $ticker,
        ), $market);
    }

