        return Async\async(function () use ($type, $subType, $params) {
            $method = 'spotPublicGetV1CommonSymbols';
            $query = $this->omit($params, array( 'type', 'subType' ));
            $spot = ($type === 'spot');
            $contract = ($type !== 'spot');
            $future = ($type === 'future');
            $swap = ($type === 'swap');
            $linear = null;
            $inverse = null;
