            //                 "init-nav":10.000000000000000000,
            //                 "api-trading":"enabled",
            //                 "tags":"etp,nav,holdinglimit"
            //             ),
            //         )
            //     }
            //
            // $inverse $future
            //
            //     {
            //         "status":"ok",
            //         "data":array(
            //             array(
            //                 "symbol":"BTC",
            //                 "contract_code":"BTC211126",
