            //
            //     {
            //         "status":"ok",
            //         "data":array(
            //             array(
            //                 "symbol":"BTC",
            //                 "contract_code":"BTC-USDT-211231",
            //                 "contract_size":0.001000000000000000,
            //                 "price_tick":0.100000000000000000,
            //                 "delivery_date":"20211231",
