            $subType = null;
            $method = 'spotPublicGetMarketTickers';
            list($type, $params) = $this->handle_market_type_and_params('fetchTickers', $market, $params);
            list($subType, $params) = $this->handle_sub_type_and_params('fetchTickers', $market, $params);
            $request = array();
