                $address = $allAddresses[$i];
                $noteMatch = ($note === null) || ($address['note'] === $note);
                $networkMatch = ($networkCode === null) || ($address['network'] === $networkCode);
                if ($noteMatch && $networkMatch) {
                    $addresses[] = $address;
                }
            }
            return $addresses;
        }) ();
    }

