            //                 "lot_decimals" => 8,
            //                 "lot_multiplier" => 1,
            //                 "leverage_buy" => array(),
            //                 "leverage_sell" => array(),
            //                 "fees" => [
            //                     [0, 0.26],
            //                     [50000, 0.24],
            //                     [100000, 0.22],
            //                     [250000, 0.2],
            //                     [500000, 0.18],
            //                     [1000000, 0.16],
