            //                     [10000000, 0.1]
            //                 ],
            //                 "fees_maker" => [
            //                     [0, 0.16],
            //                     [50000, 0.14],
            //                     [100000, 0.12],
            //                     [250000, 0.1],
            //                     [500000, 0.08],
            //                     [1000000, 0.06],
            //                     [2500000, 0.04],
