            $response = Async\await($this->privatePostTradesHistory (array_merge($request, $params)));
            //
            //     {
            //         "error" => array(),
            //         "result" => array(
            //             "trades" => array(
            //                 "GJ3NYQ-XJRTF-THZABF" => array(
            //                     "ordertxid" => "TKH2SE-ZIF5E-CFI7LT",
            //                     "postxid" => "OEN3VX-M7IF5-JNBJAM",
            //                     "pair" => "XICNXETH",
            //                     "time" => 1527213229.4491,
            //                     "type" => "sell",
            //                     "ordertype" => "limit",
