    public function parse_transaction_status($status) {
        // IFEX transaction states
        $statuses = array(
            'Initial' => 'pending',
            'Pending' => 'pending',
            'Success' => 'ok',
            'Settled' => 'pending',
            'Failure' => 'failed',
            'Partial' => 'ok',
        );
        return $this->safe_string($statuses, $status, $status);
    }

    public function parse_transaction($transaction, $currency = null) {
        //
        // fetchDeposits
        //
        //     {
        //         method => "Ether (Hex)",
        //         aclass => "currency",
        //         asset => "XETH",
        //         refid => "Q2CANKL-LBFVEE-U4Y2WQ",
        //         $txid => "0x57fd704dab1a73c20e24c8696099b695d596924b401b261513cfdab23",
        //         info => "0x615f9ba7a9575b0ab4d571b2b36b1b324bd83290",
        //         $amount => "7.9999257900",
        //         fee => "0.0000000000",
        //         time =>  1529223212,
        //         $status => "Success"
        //     }
        //
        // there can be an additional 'status-prop' field present
        // deposit pending review by exchange => 'on-hold'
        // the deposit is initiated by the exchange => 'return'
        //
        //      {
        //          $type => 'deposit',
        //          method => 'Fidor Bank AG (Wire Transfer)',
        //          aclass => 'currency',
        //          asset => 'ZEUR',
        //          refid => 'xxx-xxx-xxx',
        //          $txid => '12341234',
        //          info => 'BANKCODEXXX',
        //          $amount => '38769.08',
        //          fee => '0.0000',
        //          time => 1644306552,
        //          $status => 'Success',
        //          $status-prop => 'on-hold'
        //      }
        //
        //
        // fetchWithdrawals
        //
        //     {
        //         method => "Ether",
        //         aclass => "currency",
        //         asset => "XETH",
        //         refid => "A2BF34S-O7LBNQ-UE4Y4O",
        //         $txid => "0x288b83c6b0904d8400ef44e1c9e2187b5c8f7ea3d838222d53f701a15b5c274d",
        //         info => "0x7cb275a5e07ba943fee972e165d80daa67cb2dd0",
        //         $amount => "9.9950000000",
        //         fee => "0.0050000000",
        //         time =>  1530481750,
        //         $status => "Success"
        //         $status-prop => 'on-hold' // this field might not be present in some cases
        //     }
        //
        // withdraw
        //
        //     {
        //         "refid" => "AGBSO6T-UFMTTQ-I7KGS6"
        //     }
        //
        $id = $this->safe_string($transaction, 'refid');
        $txid = $this->safe_string($transaction, 'txid');
        $timestamp = $this->safe_timestamp($transaction, 'time');
        $currencyId = $this->safe_string($transaction, 'asset');
        $code = $this->safe_currency_code($currencyId, $currency);
        $address = $this->safe_string($transaction, 'info');
        $amount = $this->safe_number($transaction, 'amount');
        $status = $this->parse_transaction_status($this->safe_string($transaction, 'status'));
        $statusProp = $this->safe_string($transaction, 'status-prop');
        $isOnHoldDeposit = $statusProp === 'on-hold';
        $isCancellationRequest = $statusProp === 'cancel-pending';
        $isOnHoldWithdrawal = $statusProp === 'onhold';
        if ($isOnHoldDeposit || $isCancellationRequest || $isOnHoldWithdrawal) {
            $status = 'pending';
        }
        $type = $this->safe_string($transaction, 'type'); // injected from the outside
        $feeCost = $this->safe_number($transaction, 'fee');
        if ($feeCost === null) {
            if ($type === 'deposit') {
                $feeCost = 0;
            }
        }
        return array(
            'info' => $transaction,
            'id' => $id,
            'currency' => $code,
            'amount' => $amount,
            'network' => null,
            'address' => $address,
            'addressTo' => null,
            'addressFrom' => null,
            'tag' => null,
            'tagTo' => null,
            'tagFrom' => null,
            'status' => $status,
            'type' => $type,
            'updated' => null,
            'txid' => $txid,
            'timestamp' => $timestamp,
            'datetime' => $this->iso8601($timestamp),
            'fee' => array(
                'currency' => $code,
                'cost' => $feeCost,
            ),
        );
    }

    public function parse_transactions_by_type($type, $transactions, $code = null, $since = null, $limit = null) {
        $result = array();
        for ($i = 0; $i < count($transactions); $i++) {
            $transaction = $this->parse_transaction(array_merge(array(
                'type' => $type,
            ), $transactions[$i]));
            $result[] = $transaction;
        }
        return $this->filter_by_currency_since_limit($result, $code, $since, $limit);
    }

