            $promises[] = $this->publicGetAssetPairs ($params);
            if ($this->options['adjustForTimeDifference']) {
                $promises[] = $this->load_time_difference();
            }
            $responses = Async\await(Promise\all($promises));
            $assetsResponse = $responses[0];
