    public function parse_ledger_entry(array $item, ?array $currency = null): array {
        //
        //     {
        //         'LTFK7F-N2CUX-PNY4SX' => array(
        //             "refid" => "TSJTGT-DT7WN-GPPQMJ",
        //             "time" =>  1520102320.555,
        //             "type" => "trade",
        //             "aclass" => "currency",
        //             "asset" => "XETH",
        //             "amount" => "0.1087194600",
        //             "fee" => "0.0000000000",
        //             "balance" => "0.2855851000"
        //         ),
        //         ...
        //     }
        //
        $id = $this->safe_string($item, 'id');
        $direction = null;
        $account = null;
        $referenceId = $this->safe_string($item, 'refid');
        $referenceAccount = null;
        $type = $this->parse_ledger_entry_type($this->safe_string($item, 'type'));
        $currencyId = $this->safe_string($item, 'asset');
        $code = $this->safe_currency_code($currencyId, $currency);
        $currency = $this->safe_currency($currencyId, $currency);
        $amount = $this->safe_string($item, 'amount');
        if (Precise::string_lt($amount, '0')) {
            $direction = 'out';
            $amount = Precise::string_abs($amount);
        } else {
            $direction = 'in';
        }
        $timestamp = $this->safe_integer_product($item, 'time', 1000);
        return $this->safe_ledger_entry(array(
            'info' => $item,
            'id' => $id,
            'direction' => $direction,
            'account' => $account,
            'referenceId' => $referenceId,
            'referenceAccount' => $referenceAccount,
            'type' => $type,
            'currency' => $code,
            'amount' => $this->parse_number($amount),
            'before' => null,
            'after' => $this->safe_number($item, 'balance'),
            'status' => 'ok',
            'timestamp' => $timestamp,
            'datetime' => $this->iso8601($timestamp),
            'fee' => array(
                'cost' => $this->safe_number($item, 'fee'),
                'currency' => $code,
            ),
        ), $currency);
    }

    public function fetch_ledger(?string $code = null, ?int $since = null, ?int $limit = null, $params = array ()): PromiseInterface {
        return Async\async(function () use ($code, $since, $limit, $params) {
            /**
             * fetch the history of changes, actions done by the user or operations that altered the balance of the user
             *
             * @see https://docs.kraken.com/rest/#tag/Account-Data/operation/getLedgers
             *
             * @param {string} [$code] unified $currency $code, default is null
             * @param {int} [$since] timestamp in ms of the earliest $ledger entry, default is null
             * @param {int} [$limit] max number of $ledger entries to return, default is null
             * @param {array} [$params] extra parameters specific to the exchange API endpoint
             * @param {int} [$params->until] timestamp in ms of the latest $ledger entry
             * @param {int} [$params->end] timestamp in seconds of the latest $ledger entry
             * @return {array} a ~@link https://docs.ccxt.com/#/?id=$ledger ledger structure~
             */
            // https://www.kraken.com/features/api#get-ledgers-info
            Async\await($this->load_markets());
            $request = array();
            $currency = null;
            if ($code !== null) {
                $currency = $this->currency($code);
                $request['asset'] = $currency['id'];
            }
            if ($since !== null) {
                $request['start'] = $this->parse_to_int($since / 1000);
            }
            $until = $this->safe_string_n($params, array( 'until', 'till' ));
            if ($until !== null) {
                $params = $this->omit($params, array( 'until', 'till' ));
                $untilDivided = Precise::string_div($until, '1000');
                $request['end'] = $this->parse_to_int(Precise::string_add($untilDivided, '1'));
            }
            $response = Async\await($this->privatePostLedgers ($this->extend($request, $params)));
            // {  error => array(),
            //   "result" => { $ledger => { 'LPUAIB-TS774-UKHP7X' => array(   refid => "A2B4HBV-L4MDIE-JU4N3N",
            //                                                   "time" =>  1520103488.314,
            //                                                   "type" => "withdrawal",
            //                                                 "aclass" => "currency",
            //                                                  "asset" => "XETH",
            //                                                 "amount" => "-0.2805800000",
            //                                                    "fee" => "0.0050000000",
            //                                                "balance" => "0.0000051000"           ),
            $result = $this->safe_value($response, 'result', array());
            $ledger = $this->safe_value($result, 'ledger', array());
            $keys = is_array($ledger) ? array_keys($ledger) : array();
            $items = array();
            for ($i = 0; $i < count($keys); $i++) {
                $key = $keys[$i];
                $value = $ledger[$key];
                $value['id'] = $key;
                $items[] = $value;
            }
            return $this->parse_ledger($items, $currency, $since, $limit);
        }) ();
    }

    public function fetch_ledger_entries_by_ids($ids, ?string $code = null, $params = array ()) {
        return Async\async(function () use ($ids, $code, $params) {
            // https://www.kraken.com/features/api#query-ledgers
            Async\await($this->load_markets());
            $ids = implode(',', $ids);
            $request = $this->extend(array(
