            ), $params);
            $response = Async\await($this->privatePostQueryLedgers ($request));
            // {  error => array(),
            //   "result" => { 'LPUAIB-TS774-UKHP7X' => {   refid => "A2B4HBV-L4MDIE-JU4N3N",
            //                                         "time" =>  1520103488.314,
            //                                         "type" => "withdrawal",
            //                                       "aclass" => "currency",
            //                                        "asset" => "XETH",
            //                                       "amount" => "-0.2805800000",
            //                                          "fee" => "0.0050000000",
            //                                      "balance" => "0.0000051000"           } } }
            $result = $response['result'];
            $keys = is_array($result) ? array_keys($result) : array();
