    public function parse_trade(array $trade, ?array $market = null): array {
        //
        // fetchTrades (public)
        //
        //     array(
        //         "0.032310", // $price
        //         "4.28169434", // $amount
        //         1541390792.763, // $timestamp
        //         "s", // sell or buy
        //         "l", // limit or $market
        //         ""
        //     )
        //
        // fetchOrderTrades (private)
        //
        //     {
        //         "id" => 'TIMIRG-WUNNE-RRJ6GT', // injected from outside
        //         "ordertxid" => 'OQRPN2-LRHFY-HIFA7D',
        //         "postxid" => 'TKH2SE-M7IF5-CFI7LT',
        //         "pair" => 'USDCUSDT',
        //         "time" => 1586340086.457,
        //         "type" => 'sell',
        //         "ordertype" => 'market',
        //         "price" => '0.99860000',
        //         "cost" => '22.16892001',
        //         "fee" => '0.04433784',
        //         "vol" => '22.20000000',
        //         "margin" => '0.00000000',
        //         "misc" => ''
        //     }
        //
        // fetchMyTrades
        //
        //     {
        //         "ordertxid" => "OSJVN7-A2AE-63WZV",
        //         "postxid" => "TBP7O6-PNXI-CONU",
        //         "pair" => "XXBTZUSD",
        //         "time" => 1710429248.3052235,
        //         "type" => "sell",
        //         "ordertype" => "liquidation $market",
        //         "price" => "72026.50000",
        //         "cost" => "7.20265",
        //         "fee" => "0.01873",
        //         "vol" => "0.00010000",
        //         "margin" => "1.44053",
        //         "leverage" => "5",
        //         "misc" => "closing",
        //         "trade_id" => 68230622,
        //         "maker" => false
        //     }
        //
        // watchTrades
        //
        //     {
        //         "symbol" => "BTC/USD",
        //         "side" => "buy",
        //         "price" => 109601.2,
        //         "qty" => 0.04561994,
        //         "ord_type" => "market",
        //         "trade_id" => 83449369,
        //         "timestamp" => "2025-05-27T11:24:03.847761Z"
        //     }
        //
        $timestamp = null;
        $datetime = null;
        $side = null;
        $type = null;
        $price = null;
        $amount = null;
        $id = null;
        $orderId = null;
        $fee = null;
        $symbol = null;
        if (gettype($trade) === 'array' && array_keys($trade) === array_keys(array_keys($trade))) {
            $timestamp = $this->safe_timestamp($trade, 2);
            $side = ($trade[3] === 's') ? 'sell' : 'buy';
            $type = ($trade[4] === 'l') ? 'limit' : 'market';
            $price = $this->safe_string($trade, 0);
            $amount = $this->safe_string($trade, 1);
            $tradeLength = count($trade);
            if ($tradeLength > 6) {
                $id = $this->safe_string($trade, 6); // artificially added #1794
            }
        } elseif (gettype($trade) === 'string') {
            $id = $trade;
        } elseif (is_array($trade) && array_key_exists('ordertxid', $trade)) {
            $marketId = $this->safe_string($trade, 'pair');
            $foundMarket = $this->find_market_by_altname_or_id($marketId);
            if ($foundMarket !== null) {
                $market = $foundMarket;
            } elseif ($marketId !== null) {
                // delisted $market ids go here
                $market = $this->get_delisted_market_by_id($marketId);
            }
            $orderId = $this->safe_string($trade, 'ordertxid');
            $id = $this->safe_string_2($trade, 'id', 'postxid');
            $timestamp = $this->safe_timestamp($trade, 'time');
            $side = $this->safe_string($trade, 'type');
            $type = $this->safe_string($trade, 'ordertype');
            $price = $this->safe_string($trade, 'price');
            $amount = $this->safe_string($trade, 'vol');
            if (is_array($trade) && array_key_exists('fee', $trade)) {
                $currency = null;
                if ($market !== null) {
                    $currency = $market['quote'];
                }
                $fee = array(
                    'cost' => $this->safe_string($trade, 'fee'),
                    'currency' => $currency,
                );
            }
        } else {
            $symbol = $this->safe_string($trade, 'symbol');
            $datetime = $this->safe_string($trade, 'timestamp');
            $id = $this->safe_string($trade, 'trade_id');
            $side = $this->safe_string($trade, 'side');
            $type = $this->safe_string($trade, 'ord_type');
            $price = $this->safe_string($trade, 'price');
            $amount = $this->safe_string($trade, 'qty');
        }
        if ($market !== null) {
            $symbol = $market['symbol'];
        }
        $cost = $this->safe_string($trade, 'cost');
        $maker = $this->safe_bool($trade, 'maker');
        $takerOrMaker = null;
        if ($maker !== null) {
            $takerOrMaker = $maker ? 'maker' : 'taker';
        }
        if ($datetime === null) {
            $datetime = $this->iso8601($timestamp);
        } else {
            $timestamp = $this->parse8601($datetime);
        }
        return $this->safe_trade(array(
            'id' => $id,
            'order' => $orderId,
            'info' => $trade,
            'timestamp' => $timestamp,
            'datetime' => $datetime,
            'symbol' => $symbol,
            'type' => $type,
            'side' => $side,
            'takerOrMaker' => $takerOrMaker,
            'price' => $price,
            'amount' => $amount,
            'cost' => $cost,
            'fee' => $fee,
        ), $market);
    }

    public function fetch_trades(string $symbol, ?int $since = null, ?int $limit = null, $params = array ()): PromiseInterface {
        return Async\async(function () use ($symbol, $since, $limit, $params) {
            /**
             * get the list of most recent $trades for a particular $symbol
             *
             * @see https://docs.kraken.com/rest/#tag/Spot-Market-Data/operation/getRecentTrades
             *
             * @param {string} $symbol unified $symbol of the $market to fetch $trades for
             * @param {int} [$since] timestamp in ms of the earliest trade to fetch
             * @param {int} [$limit] the maximum amount of $trades to fetch
             * @param {array} [$params] extra parameters specific to the exchange API endpoint
             * @return {Trade[]} a list of ~@link https://docs.ccxt.com/#/?$id=public-$trades trade structures~
             */
            Async\await($this->load_markets());
            $market = $this->market($symbol);
            $id = $market['id'];
            $request = array(
                'pair' => $id,
            );
            // https://support.kraken.com/hc/en-us/articles/218198197-How-to-pull-all-trade-data-using-the-Kraken-REST-API
            // https://github.com/ccxt/ccxt/issues/5677
            if ($since !== null) {
                $request['since'] = $this->number_to_string($this->parse_to_int($since / 1000)); // expected to be in seconds
            }
            if ($limit !== null) {
                $request['count'] = $limit;
            }
            $response = Async\await($this->publicGetTrades ($this->extend($request, $params)));
            //
            //     {
            //         "error" => array(),
            //         "result" => {
            //             "XETHXXBT" => [
            //                 ["0.032310","4.28169434",1541390792.763,"s","l",""]
            //             ],
            //             "last" => "1541439421200678657"
            //         }
            //     }
            //
            $result = $response['result'];
            $trades = $result[$id];
            // $trades is a sorted array => last (most recent trade) goes last
            $length = count($trades);
            if ($length <= 0) {
                return array();
            }
            $lastTrade = $trades[$length - 1];
            $lastTradeId = $this->safe_string($result, 'last');
            $lastTrade[] = $lastTradeId;
            $trades[$length - 1] = $lastTrade;
            return $this->parse_trades($trades, $market, $since, $limit);
        }) ();
    }

    public function parse_balance($response): array {
        $balances = $this->safe_value($response, 'result', array());
        $result = array(
            'info' => $response,
            'timestamp' => null,
            'datetime' => null,
        );
        $currencyIds = is_array($balances) ? array_keys($balances) : array();
        for ($i = 0; $i < count($currencyIds); $i++) {
            $currencyId = $currencyIds[$i];
            $code = $this->safe_currency_code($currencyId);
            $balance = $this->safe_value($balances, $currencyId, array());
            $account = $this->account();
            $account['used'] = $this->safe_string($balance, 'hold_trade');
            $account['total'] = $this->safe_string($balance, 'balance');
            $result[$code] = $account;
        }
        return $this->safe_balance($result);
    }

