    public function parse_order($order, $market = null) {
        //
        // fetchOrder, fetchOrdersByStatus
        //
        //     {
        //         "id" => "64507d02921f1c0001ff6892",
        //         "symbol" => "XBTUSDTM",
        //         "type" => "market",
        //         "side" => "buy",
        //         "price" => null,
        //         "size" => 1,
        //         "value" => "27.992",
        //         "dealValue" => "27.992",
        //         "dealSize" => 1,
        //         "stp" => "",
        //         "stop" => "",
        //         "stopPriceType" => "",
        //         "stopTriggered" => false,
        //         "stopPrice" => null,
        //         "timeInForce" => "GTC",
        //         "postOnly" => false,
        //         "hidden" => false,
        //         "iceberg" => false,
        //         "leverage" => "17",
        //         "forceHold" => false,
        //         "closeOrder" => false,
        //         "visibleSize" => null,
        //         "clientOid" => null,
        //         "remark" => null,
        //         "tags" => null,
        //         "isActive" => false,
        //         "cancelExist" => false,
        //         "createdAt" => 1682996482000,
        //         "updatedAt" => 1682996483062,
        //         "endAt" => 1682996483062,
        //         "orderTime" => 1682996482953900677,
        //         "settleCurrency" => "USDT",
        //         "status" => "done",
        //         "filledValue" => "27.992",
        //         "filledSize" => 1,
        //         "reduceOnly" => false
        //     }
        //
        $marketId = $this->safe_string($order, 'symbol');
        $market = $this->safe_market($marketId, $market);
        $symbol = $market['symbol'];
        $orderId = $this->safe_string($order, 'id');
        $type = $this->safe_string($order, 'type');
        $timestamp = $this->safe_integer($order, 'createdAt');
        $datetime = $this->iso8601($timestamp);
        $price = $this->safe_string($order, 'price');
        // $price is zero for $market $order
        // omitZero is called in safeOrder2
        $side = $this->safe_string($order, 'side');
        $feeCurrencyId = $this->safe_string($order, 'feeCurrency');
        $feeCurrency = $this->safe_currency_code($feeCurrencyId);
        $feeCost = $this->safe_number($order, 'fee');
        $amount = $this->safe_string($order, 'size');
        $filled = $this->safe_string($order, 'filledSize');
        $cost = $this->safe_string($order, 'filledValue');
        $average = null;
        if (Precise::string_gt($filled, '0')) {
            $contractSize = $this->safe_string($market, 'contractSize');
            if ($market['linear']) {
                $average = Precise::string_div($cost, Precise::string_mul($contractSize, $filled));
            } else {
                $average = Precise::string_div(Precise::string_mul($contractSize, $filled), $cost);
            }
        }
        // precision reported by their api is 8 d.p.
        // $average = Precise::string_div($cost, Precise::string_mul($filled, $market['contractSize']));
        // bool
        $isActive = $this->safe_value($order, 'isActive', false);
        $cancelExist = $this->safe_value($order, 'cancelExist', false);
        $status = $isActive ? 'open' : 'closed';
        $status = $cancelExist ? 'canceled' : $status;
        $fee = array(
            'currency' => $feeCurrency,
            'cost' => $feeCost,
        );
        $clientOrderId = $this->safe_string($order, 'clientOid');
        $timeInForce = $this->safe_string($order, 'timeInForce');
        $stopPrice = $this->safe_number($order, 'stopPrice');
        $postOnly = $this->safe_value($order, 'postOnly');
        return $this->safe_order(array(
            'id' => $orderId,
            'clientOrderId' => $clientOrderId,
            'symbol' => $symbol,
            'type' => $type,
            'timeInForce' => $timeInForce,
            'postOnly' => $postOnly,
            'side' => $side,
            'amount' => $amount,
            'price' => $price,
            'stopPrice' => $stopPrice,
            'triggerPrice' => $stopPrice,
            'cost' => $cost,
            'filled' => $filled,
            'remaining' => null,
            'timestamp' => $timestamp,
            'datetime' => $datetime,
            'fee' => $fee,
            'status' => $status,
            'info' => $order,
            'lastTradeTimestamp' => null,
            'average' => $average,
            'trades' => null,
        ), $market);
    }

