    public function fetch_currencies($params = array ()): PromiseInterface {
        return Async\async(function () use ($params) {
            /**
             * fetches all available currencies on an exchange
             * @param {dict} [$params] extra parameters specific to the exchange API endpoint
             * @return {dict} an associative dictionary of currencies
             */
            if (!$this->check_required_credentials(false)) {
                return array();
            }
            $response = Async\await($this->privateGetSendNetworks ($params));
            //
            //     {
            //         "networks" => array(
            //           array(
            //             "id" => 0,
            //             "name" => "Ethereum",
            //             "native_currency" => "ETH"
            //           ),
            //           ...
            //         )
            //     }
            //
            $currenciesData = $this->safe_list($response, 'data', array());
            $result = array();
            for ($i = 0; $i < count($currenciesData); $i++) {
                $networkEntry = $currenciesData[$i];
                $id = $this->safe_string($networkEntry, 'native_currency');
                $code = $this->safe_currency_code($id);
                if (!(is_array($result) && array_key_exists($code, $result))) {
                    $result[$code] = array(
                        'id' => $id,
                        'code' => $code,
                        'precision' => null,
                        'type' => null,
                        'name' => null,
                        'active' => null,
                        'deposit' => null,
                        'withdraw' => null,
                        'fee' => null,
                        'limits' => array(
                            'withdraw' => array(
                                'min' => null,
                                'max' => null,
                            ),
                            'deposit' => array(
                                'min' => null,
                                'max' => null,
                            ),
                        ),
                        'networks' => array(),
                        'info' => array(),
                    );
                }
                $networkId = $this->safe_string($networkEntry, 'name');
                $networkCode = $this->network_id_to_code($networkId);
                $result[$code]['networks'][$networkCode] = array(
                    'id' => $networkId,
                    'network' => $networkCode,
                    'limits' => array(
                        'withdraw' => array(
                            'min' => null,
                            'max' => null,
                        ),
                        'deposit' => array(
                            'min' => null,
                            'max' => null,
                        ),
                    ),
                    'active' => null,
                    'deposit' => null,
                    'withdraw' => null,
                    'fee' => null,
                    'precision' => null,
                    'info' => $networkEntry,
                );
                // add entry in $info
                $info = $this->safe_list($result[$code], 'info', array());
                $info[] = $networkEntry;
                $result[$code]['info'] = $info;
            }
            // only after all entries are formed in currencies, restructure each entry
            $allKeys = is_array($result) ? array_keys($result) : array();
            for ($i = 0; $i < count($allKeys); $i++) {
                $code = $allKeys[$i];
                $result[$code] = $this->safe_currency_structure($result[$code]); // this is needed after adding network entry
            }
            return $result;
        }) ();
    }

    public function fetch_markets($params = array ()): PromiseInterface {
        return Async\async(function () use ($params) {
            /**
             * retrieves data on all $markets for luno
             *
             * @see https://www.luno.com/en/developers/api#tag/Market/operation/Markets
             *
             * @param {array} [$params] extra parameters specific to the exchange API endpoint
             * @return {array[]} an array of objects representing $market data
             */
            $response = Async\await($this->exchangeGetMarkets ($params));
            //
            //     {
            //         "markets":array(
            //             array(
            //                 "market_id":"BCHXBT",
            //                 "trading_status":"ACTIVE",
            //                 "base_currency":"BCH",
            //                 "counter_currency":"XBT",
            //                 "min_volume":"0.01",
            //                 "max_volume":"100.00",
            //                 "volume_scale":2,
            //                 "min_price":"0.0001",
            //                 "max_price":"1.00",
            //                 "price_scale":6,
            //                 "fee_scale":8,
            //             ),
            //         )
            //     }
            //
            $result = array();
            $markets = $this->safe_value($response, 'markets', array());
            for ($i = 0; $i < count($markets); $i++) {
                $market = $markets[$i];
                $id = $this->safe_string($market, 'market_id');
                $baseId = $this->safe_string($market, 'base_currency');
                $quoteId = $this->safe_string($market, 'counter_currency');
                $base = $this->safe_currency_code($baseId);
                $quote = $this->safe_currency_code($quoteId);
                $status = $this->safe_string($market, 'trading_status');
