    public function parse_order(array $order, ?array $market = null): array {
        //
        //     {
        //         "base" => "string",
        //         "completed_timestamp" => "string",
        //         "counter" => "string",
        //         "creation_timestamp" => "string",
        //         "expiration_timestamp" => "string",
        //         "fee_base" => "string",
        //         "fee_counter" => "string",
        //         "limit_price" => "string",
        //         "limit_volume" => "string",
        //         "order_id" => "string",
        //         "pair" => "string",
        //         "state" => "PENDING",
        //         "type" => "BID"
        //     }
        //
        $timestamp = $this->safe_integer($order, 'creation_timestamp');
        $status = $this->parse_order_status($this->safe_string($order, 'state'));
        $status = ($status === 'open') ? $status : $status;
        $side = null;
        $orderType = $this->safe_string($order, 'type');
        if (($orderType === 'ASK') || ($orderType === 'SELL')) {
            $side = 'sell';
        } elseif (($orderType === 'BID') || ($orderType === 'BUY')) {
            $side = 'buy';
        }
        $marketId = $this->safe_string($order, 'pair');
        $market = $this->safe_market($marketId, $market);
        $price = $this->safe_string($order, 'limit_price');
        $amount = $this->safe_string($order, 'limit_volume');
        $quoteFee = $this->safe_number($order, 'fee_counter');
        $baseFee = $this->safe_number($order, 'fee_base');
        $filled = $this->safe_string($order, 'base');
        $cost = $this->safe_string($order, 'counter');
        $fee = null;
        if ($quoteFee !== null) {
            $fee = array(
                'cost' => $quoteFee,
                'currency' => $market['quote'],
            );
        } elseif ($baseFee !== null) {
            $fee = array(
                'cost' => $baseFee,
                'currency' => $market['base'],
            );
        }
        $id = $this->safe_string($order, 'order_id');
        return $this->safe_order(array(
            'id' => $id,
            'clientOrderId' => null,
            'datetime' => $this->iso8601($timestamp),
            'timestamp' => $timestamp,
            'lastTradeTimestamp' => null,
            'status' => $status,
            'symbol' => $market['symbol'],
            'type' => null,
            'timeInForce' => null,
            'postOnly' => null,
            'side' => $side,
            'price' => $price,
            'triggerPrice' => null,
            'amount' => $amount,
            'filled' => $filled,
            'cost' => $cost,
            'remaining' => null,
            'trades' => null,
            'fee' => $fee,
            'info' => $order,
            'average' => null,
        ), $market);
    }

    public function fetch_order(string $id, ?string $symbol = null, $params = array ()) {
        return Async\async(function () use ($id, $symbol, $params) {
            /**
             * fetches information on an order made by the user
             *
             * @see https://www.luno.com/en/developers/api#tag/Orders/operation/GetOrder
             *
             * @param {string} $id order $id
