    public function create_order(string $symbol, string $type, string $side, float $amount, ?float $price = null, $params = array ()) {
        return Async\async(function () use ($symbol, $type, $side, $amount, $price, $params) {
            /**
             * create a trade order
             *
             * @see https://www.luno.com/en/developers/api#tag/Orders/operation/PostMarketOrder
             * @see https://www.luno.com/en/developers/api#tag/Orders/operation/PostLimitOrder
             *
             * @param {string} $symbol unified $symbol of the $market to create an order in
             * @param {string} $type 'market' or 'limit'
             * @param {string} $side 'buy' or 'sell'
             * @param {float} $amount how much of currency you want to trade in units of base currency
             * @param {float} [$price] the $price at which the order is to be fulfilled, in units of the quote currency, ignored in $market orders
             * @param {array} [$params] extra parameters specific to the exchange API endpoint
             * @return {array} an ~@link https://docs.ccxt.com/#/?id=order-structure order structure~
             */
            Async\await($this->load_markets());
            $market = $this->market($symbol);
            $request = array(
                'pair' => $market['id'],
            );
            $response = null;
            if ($type === 'market') {
                $request['type'] = strtoupper($side);
                // todo add createMarketBuyOrderRequires $price logic is implemented in the other exchanges
                if ($side === 'buy') {
                    $request['counter_volume'] = $this->amount_to_precision($market['symbol'], $amount);
                } else {
                    $request['base_volume'] = $this->amount_to_precision($market['symbol'], $amount);
                }
                $response = Async\await($this->privatePostMarketorder ($this->extend($request, $params)));
            } else {
                $request['volume'] = $this->amount_to_precision($market['symbol'], $amount);
                $request['price'] = $this->price_to_precision($market['symbol'], $price);
                $request['type'] = ($side === 'buy') ? 'BID' : 'ASK';
                $response = Async\await($this->privatePostPostorder ($this->extend($request, $params)));
            }
            return $this->safe_order(array(
                'info' => $response,
                'id' => $response['order_id'],
            ), $market);
        }) ();
    }

