    public function parse_ticker($ticker, $market = null) {
        //
        // fetchTicker
        //
        //     {
        //         "OMSId":1,
        //         "InstrumentId":8,
        //         "BestBid":19069.31,
        //         "BestOffer":19069.32,
        //         "LastTradedPx":19069.32,
        //         "LastTradedQty":0.0001,
        //         "LastTradeTime":1607040406424,
        //         "SessionOpen":19069.32,
        //         "SessionHigh":19069.32,
        //         "SessionLow":19069.32,
        //         "SessionClose":19069.32,
        //         "Volume":0.0001,
        //         "CurrentDayVolume":0.0001,
        //         "CurrentDayNotional":1.906932,
        //         "CurrentDayNumTrades":1,
        //         "CurrentDayPxChange":0.00,
        //         "Rolling24HrVolume":0.000000000000000000000000000,
        //         "Rolling24HrNotional":0.00000000000000000000000,
        //         "Rolling24NumTrades":0,
        //         "Rolling24HrPxChange":0,
        //         "TimeStamp":"1607040406425",
        //         "BidQty":0,
        //         "AskQty":0,
        //         "BidOrderCt":0,
        //         "AskOrderCt":0,
        //         "Rolling24HrPxChangePercent":0,
        //     }
        //
        $timestamp = $this->safe_integer($ticker, 'TimeStamp');
        $marketId = $this->safe_string($ticker, 'InstrumentId');
        $symbol = $this->safe_symbol($marketId, $market);
        $last = $this->safe_number($ticker, 'LastTradedPx');
        $percentage = $this->safe_number($ticker, 'Rolling24HrPxChangePercent');
        $change = $this->safe_number($ticker, 'Rolling24HrPxChange');
        $open = $this->safe_number($ticker, 'SessionOpen');
        $average = null;
        if (($last !== null) && ($change !== null)) {
            $average = $this->sum($last, $open) / 2;
        }
        $baseVolume = $this->safe_number($ticker, 'Rolling24HrVolume');
        $quoteVolume = $this->safe_number($ticker, 'Rolling24HrNotional');
        $vwap = $this->vwap($baseVolume, $quoteVolume);
        return array(
            'symbol' => $symbol,
            'timestamp' => $timestamp,
            'datetime' => $this->iso8601($timestamp),
            'high' => $this->safe_number($ticker, 'SessionHigh'),
            'low' => $this->safe_number($ticker, 'SessionLow'),
