            //                "maxWd" => "8852150",
            //                "minFee" => "8",
            //                "minWd" => "2",
            //                "name" => "Tether",
            //                "usedWdQuota" => "0",
            //                "wdQuota" => "500",
            //                "wdTickSz" => "3"
            //            ),
            //            ...
            //        ),
            //        "msg" => ""
            //    }
            //
            $data = $this->safe_value($response, 'data', array());
            $result = array();
            $dataByCurrencyId = $this->group_by($data, 'ccy');
            $currencyIds = is_array($dataByCurrencyId) ? array_keys($dataByCurrencyId) : array();
