            Async\await($this->load_markets());
            $request = array(
                // 'instType' => 'SPOT', // SPOT, MARGIN, SWAP, FUTURES, OPTION
                // 'ccy' => currency['id'],
                // 'mgnMode' => 'isolated', // isolated, cross
                // 'ctType' => 'linear', // linear, inverse, only applicable to FUTURES/SWAP
