            //            change => '3.13'
            //        ),
            //        cache_time => '1699252953.832795',
            //        current_time => '1699252958.859391'
            //    }
            //
            $result = $this->safe_value($response, 'result', array());
            $timestamp = $this->safe_integer_product($response, 'cache_time', 1000);
            return $this->extend(
